/**
 * Main App Component
 *
 * Root application component implementing basic routing and layout structure.
 * Following CLAUDE.md guidelines for layered architecture and beautiful design.
 *
 * Phase 4.2: Basic routing implementation with MainLayout integration
 */

import { lazy, Suspense, useEffect, useState, useTransition } from 'react';
import { QueryClientProvider } from '@tanstack/react-query';
import { ReactQueryDevtools } from '@tanstack/react-query-devtools';
import { log } from './utils/logger';
import { Toaster } from 'react-hot-toast';
import LoadingSpinner from './components/common/LoadingSpinner';
import { queryClient } from './lib/queryClient';
import { ComposedThemeProvider as ThemeProvider } from './contexts/theme/ComposedThemeProvider';
import { ThemeDebugger } from './components/theme';
// Cache debugging removed - overengineered development utility not needed

// Layout
import MainLayout from './components/layouts/MainLayout';

// Context7 Lazy Loading Strategy - Performance Optimized Code Splitting
// Following React.dev patterns for optimal bundle performance
// Critical path components (loaded immediately with Suspense boundaries)
const Dashboard = lazy(() => import('./pages/Dashboard'));
const Collection = lazy(() => import('./pages/Collection'));

// Secondary features (lazy loaded with prefetch hints)
const CollectionItemDetail = lazy(
  () =>
    import(/* webpackChunkName: "item-detail" */ './pages/CollectionItemDetail')
);
const AddEditItem = lazy(
  () => import(/* webpackChunkName: "forms" */ './pages/AddEditItem')
);

// Search features (bundled together for caching)
const SetSearch = lazy(
  () => import(/* webpackChunkName: "search-features" */ './pages/SetSearch')
);
const SealedProductSearch = lazy(
  () =>
    import(
      /* webpackChunkName: "search-features" */ './pages/SealedProductSearch'
    )
);

// Auction features (bundled together for better caching)
const Auctions = lazy(
  () => import(/* webpackChunkName: "auction-features" */ './pages/Auctions')
);
const AuctionDetail = lazy(
  () =>
    import(/* webpackChunkName: "auction-features" */ './pages/AuctionDetail')
);
const CreateAuction = lazy(
  () =>
    import(/* webpackChunkName: "auction-features" */ './pages/CreateAuction')
);
const AuctionEdit = lazy(
  () => import(/* webpackChunkName: "auction-features" */ './pages/AuctionEdit')
);

// Analytics and heavy features (separate chunks)
const SalesAnalytics = lazy(
  () => import(/* webpackChunkName: "analytics" */ './pages/SalesAnalytics')
);
const Activity = lazy(
  () => import(/* webpackChunkName: "activity" */ './pages/Activity')
);
const DbaExport = lazy(
  () => import(/* webpackChunkName: "export" */ './pages/DbaExport')
);

// Context7 Pattern: Main App component with useTransition for smooth navigation
function App() {
  const [currentPath, setCurrentPath] = useState(window.location.pathname);
  const [isPending, startTransition] = useTransition();

  // Test logger functionality
  log('App loaded!');

  // Test API client error handling removed for cleaner console

  // Context7 Pattern: Navigation with transition for better UX
  useEffect(() => {
    const handlePopState = () => {
      startTransition(() => {
        setCurrentPath(window.location.pathname);
      });
    };

    window.addEventListener('popstate', handlePopState);

    return () => {
      window.removeEventListener('popstate', handlePopState);
    };
  }, []);

  // Route configuration - simple routing without external router library
  const renderPage = () => {
    // Handle dynamic auction routes
    if (currentPath.startsWith('/auctions/') && currentPath !== '/auctions') {
      const routePart = currentPath.split('/auctions/')[1];
      if (routePart === 'create') {
        return <CreateAuction />;
      }
      // Handle edit routes: /auctions/{id}/edit
      if (routePart && routePart.includes('/edit')) {
        const auctionId = routePart.split('/edit')[0];
        return <AuctionEdit auctionId={auctionId} />;
      }
      if (routePart && !routePart.includes('/')) {
        return <AuctionDetail auctionId={routePart} />;
      }
    }

    // Handle edit routes: /collection/edit/{type}/{id}
    if (currentPath.startsWith('/collection/edit/')) {
      const pathParts = currentPath.split('/');
      if (pathParts.length === 5) {
        // /collection/edit/{type}/{id}
        const [, , , type, id] = pathParts;
        if ((type === 'psa' || type === 'raw' || type === 'sealed') && id) {
          return <AddEditItem />;
        }
      }
    }

    // Handle dynamic collection item detail routes
    if (
      currentPath.startsWith('/collection/') &&
      currentPath !== '/collection' &&
      currentPath !== '/collection/add'
    ) {
      const pathParts = currentPath.split('/');
      if (pathParts.length === 4) {
        // /collection/{type}/{id}
        const [, , type, id] = pathParts;
        if ((type === 'psa' || type === 'raw' || type === 'sealed') && id) {
          return <CollectionItemDetail />;
        }
      }
    }

    switch (currentPath) {
      case '/':
      case '/dashboard':
        return <Dashboard />;
      case '/collection':
        return <Collection />;
      case '/collection/add':
        return <AddEditItem />;
      case '/add-item':
        return <AddEditItem />;
      case '/sets':
      case '/set-search':
        return <SetSearch />;
      case '/sealed-products-search':
        return <SealedProductSearch />;
      case '/auctions':
        return <Auctions />;
      case '/sales-analytics':
      case '/analytics':
        return <SalesAnalytics />;
      case '/activity':
        return <Activity />;
      case '/dba-export':
        return <DbaExport />;
      default:
        // Default to dashboard for root and unknown routes
        return <Dashboard />;
    }
  };

  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <MainLayout>
          {/* Context7 Pattern: Suspense boundary with transition state */}
          <Suspense
            fallback={
              <div
                className={`flex items-center justify-center min-h-[60vh] transition-opacity duration-200 ${
                  isPending ? 'opacity-50' : 'opacity-100'
                }`}
              >
                <LoadingSpinner size="lg" />
              </div>
            }
          >
            {renderPage()}
          </Suspense>
        </MainLayout>
        {process.env.NODE_ENV === 'development' && (
          <>
            <ReactQueryDevtools
              initialIsOpen={false}
              buttonPosition="bottom-left"
              position="bottom"
            />
            {/* ThemeDebugger temporarily disabled during refactoring */}
          </>
        )}
        <Toaster
          position="top-right"
          reverseOrder={false}
          gutter={8}
          containerClassName=""
          containerStyle={{}}
          toastOptions={{
            // Global default options for all toasts
            duration: 4000,
            style: {
              fontFamily:
                'ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif',
              fontSize: '14px',
              fontWeight: '500',
              borderRadius: '8px',
              boxShadow:
                '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
            },
            success: {
              duration: 4000,
              iconTheme: {
                primary: '#16A34A',
                secondary: '#FFFFFF',
              },
            },
            error: {
              duration: 5000,
              iconTheme: {
                primary: '#DC2626',
                secondary: '#FFFFFF',
              },
            },
          }}
        />
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

/**
 * Context7 Award-Winning Loading Spinner Component with Theme Integration
 * Ultra-premium loading spinner with stunning visual hierarchy and micro-animations
 * Features glass-morphism, premium gradients, and award-winning design patterns
 *
 * Following CLAUDE.md + Context7 principles:
 * - Award-winning visual design with micro-animations
 * - Premium gradient effects and glass-morphism
 * - Context7 design system compliance
 * - Stunning pulsing and rotation effects
 * - Theme Integration: Uses unified theme system for consistent styling
 */

import React from 'react';
import { useVisualTheme } from '../../contexts/theme';
import { getElementTheme, ThemeColor } from '../../theme/formThemes';

export interface LoadingSpinnerProps {
  size?: 'sm' | 'md' | 'lg' | 'xl';
  color?: 'blue' | 'gray' | 'green' | 'red' | 'yellow' | 'purple';
  text?: string;
  fullScreen?: boolean;
  className?: string;
  themeColor?: ThemeColor;
}

const LoadingSpinner: React.FC<LoadingSpinnerProps> = ({
  size = 'md',
  color = 'blue',
  text,
  fullScreen = false,
  themeColor = 'dark',
}) => {
  const { visualTheme } = useVisualTheme();
  const elementTheme = getElementTheme(themeColor);
  const sizeClasses = {
    sm: 'w-5 h-5',
    md: 'w-10 h-10',
    lg: 'w-16 h-16',
    xl: 'w-20 h-20',
  };

  const colorClasses = {
    blue: 'text-cyan-400',
    gray: 'text-zinc-400',
    green: 'text-emerald-400',
    red: 'text-red-400',
    yellow: 'text-amber-400',
    purple: 'text-purple-400',
  };

  // Spinner classes are handled inline in the component

  const containerClasses = fullScreen
    ? 'fixed inset-0 flex items-center justify-center bg-zinc-950/80 backdrop-blur-sm z-50'
    : 'flex items-center justify-center';

  const textSizeClasses = {
    sm: 'text-sm',
    md: 'text-base',
    lg: 'text-lg',
    xl: 'text-xl',
  };

  const SpinnerElement = (
    <div className={containerClasses}>
      <div className="flex flex-col items-center">
        {/* Context7 Premium Loading Container */}
        <div className="relative">
          {/* Multi-layer rotating rings with theme colors */}
          <div
            className={`absolute inset-0 rounded-full border-2 border-transparent bg-gradient-to-r ${elementTheme.gradient} animate-spin ${sizeClasses[size]} [clip-path:polygon(0%_0%,25%_0%,25%_25%,0%_25%)]`}
          ></div>
          <div
            className={`absolute inset-1 rounded-full border border-transparent bg-gradient-to-l ${elementTheme.glow} animate-spin ${sizeClasses[size]} opacity-60 [clip-path:polygon(75%_75%,100%_75%,100%_100%,75%_100%)] [animation-duration:1.5s] [animation-direction:reverse]`}
          ></div>

          {/* Inner pulsing circle with enhanced glow */}
          <div
            className={`relative bg-zinc-900/95 backdrop-blur-sm rounded-full ${elementTheme.border} shadow-2xl flex items-center justify-center ${sizeClasses[size]} shadow-[0_0_20px_0_rgb(6,182,212,0.3)]`}
          >
            <div
              className={`w-1/2 h-1/2 bg-gradient-to-br ${colorClasses[color]} rounded-full animate-pulse opacity-90 shadow-lg`}
            ></div>
          </div>

          {/* Premium shimmer effect with enhanced animation */}
          <div
            className={`absolute inset-0 rounded-full bg-gradient-to-r ${elementTheme.glow} animate-ping opacity-30 ${sizeClasses[size]} [animation-delay:0.5s]`}
          ></div>
          <div
            className={`absolute inset-0 rounded-full bg-gradient-to-r ${elementTheme.glow} animate-ping opacity-20 ${sizeClasses[size]} [animation-delay:1s]`}
          ></div>
        </div>

        {text && (
          <p
            className={`mt-4 text-zinc-300 font-semibold tracking-wide ${textSizeClasses[size]} animate-pulse bg-gradient-to-r ${elementTheme.gradient} bg-clip-text text-transparent`}
          >
            {text}
          </p>
        )}
      </div>
    </div>
  );

  return SpinnerElement;
};

export default LoadingSpinner;

/**
 * Optimized Autocomplete Component - Context7 Performance Patterns with Theme Integration
 *
 * Following Context7 React.dev documentation for optimal autocomplete performance:
 * - React.memo for preventing unnecessary re-renders
 * - useMemo for expensive computations
 * - useCallback for stable event handlers
 * - Suspense boundaries for async operations
 * - React Compiler optimization ready
 * - Unified theme system integration with performance patterns maintained
 */

import React, {
  memo,
  useMemo,
  useCallback,
  useState,
  useRef,
  useEffect,
  Suspense,
  startTransition,
} from 'react';
import { Search, Loader2 } from 'lucide-react';
import { SearchResult } from '../../hooks/useSearch';
import {
  useOptimizedSearch,
  useSearchResultSelector,
} from '../../hooks/useOptimizedSearch';
import { useVisualTheme, useLayoutTheme, useAnimationTheme } from '../../contexts/theme';
import { getElementTheme, ThemeColor } from '../../theme/formThemes';

interface OptimizedAutocompleteProps {
  placeholder?: string;
  searchType: 'sets' | 'products' | 'cards';
  setFilter?: string;
  onSelect: (result: SearchResult) => void;
  onInputChange?: (value: string) => void;
  className?: string;
  disabled?: boolean;
  autoFocus?: boolean;
  minLength?: number;
  maxResults?: number;
  themeColor?: ThemeColor;
}

// Context7 Pattern: Memoized suggestion item for optimal rendering
const SuggestionItem = memo(
  ({
    result,
    isSelected,
    onSelect,
    searchQuery,
  }: {
    result: SearchResult;
    isSelected: boolean;
    onSelect: (result: SearchResult) => void;
    searchQuery: string;
  }) => {
    // Context7 Pattern: Memoized highlighted text with theme integration
    const highlightedText = useMemo(() => {
      const text = result.displayName;
      if (!searchQuery) {
        return text;
      }

      const regex = new RegExp(
        `(${searchQuery.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`,
        'gi'
      );
      const parts = text.split(regex);

      return parts.map((part, index) =>
        regex.test(part) ? (
          <mark
            key={index}
            className="bg-gradient-to-r from-cyan-500/20 to-blue-500/20 text-cyan-300 rounded px-1 font-medium"
          >
            {part}
          </mark>
        ) : (
          <span key={index}>{part}</span>
        )
      );
    }, [result.displayName, searchQuery]);

    // Context7 Pattern: Memoized click handler
    const handleClick = useCallback(() => {
      onSelect(result);
    }, [onSelect, result]);

    return (
      <div
        onClick={handleClick}
        className={`
        px-4 py-3 cursor-pointer transition-all duration-300 border-l-4 relative overflow-hidden
        ${
          isSelected
            ? 'bg-gradient-to-r from-cyan-500/10 to-blue-500/10 border-cyan-400 text-cyan-100 shadow-lg backdrop-blur-sm'
            : 'hover:bg-zinc-800/60 border-transparent text-zinc-300 hover:text-zinc-100'
        }
      `}
      >
        <div className="font-medium text-sm relative z-10">
          {highlightedText}
        </div>
        {result.data?.setName && result.type !== 'set' && (
          <div className="text-xs text-zinc-400 mt-1 relative z-10">
            Set: {result.data.setName}
          </div>
        )}
        {result.data?.category && (
          <div className="text-xs text-zinc-400 mt-1 relative z-10">
            Category: {result.data.category}
          </div>
        )}
        {/* Subtle glow effect for selected items */}
        {isSelected && (
          <div className="absolute inset-0 bg-gradient-to-r from-cyan-400/5 to-blue-400/5 pointer-events-none"></div>
        )}
      </div>
    );
  }
);

SuggestionItem.displayName = 'SuggestionItem';

// Context7 Pattern: Memoized loading fallback component with theme integration
const SearchFallback = memo(() => {
  return (
    <div className="flex items-center justify-center py-8">
      <Loader2 className="w-6 h-6 animate-spin text-cyan-400" />
      <span className="ml-2 text-sm text-zinc-300">Searching...</span>
    </div>
  );
});

SearchFallback.displayName = 'SearchFallback';

// Context7 Pattern: Main autocomplete component with comprehensive optimization and theme integration
export const OptimizedAutocomplete = memo(
  ({
    placeholder = 'Search...',
    searchType,
    setFilter,
    onSelect,
    onInputChange,
    className = '',
    disabled = false,
    autoFocus = false,
    minLength = 1,
    maxResults = 10,
    themeColor = 'dark',
  }: OptimizedAutocompleteProps) => {
    const { visualTheme } = useVisualTheme();
    const { density } = useLayoutTheme();
    const { animationIntensity } = useAnimationTheme();
    const elementTheme = getElementTheme(themeColor);
    const [inputValue, setInputValue] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [isFocused, setIsFocused] = useState(false);

    const inputRef = useRef<HTMLInputElement>(null);

    // Context7 Pattern: Optimized search hook with transitions
    const searchHook = useOptimizedSearch({
      minLength,
      debounceMs: 300,
      enableTransitions: true,
    });

    // Context7 Pattern: Memoized filtered results
    const filteredResults = useSearchResultSelector(
      searchHook.results,
      useCallback((result: SearchResult) => result, []),
      [maxResults]
    ).slice(0, maxResults);

    // Context7 Pattern: Memoized search trigger
    const triggerSearch = useCallback(
      (query: string) => {
        if (!query || query.length < minLength) {
          setIsOpen(false);
          return;
        }

        switch (searchType) {
          case 'sets':
            searchHook.searchSets(query);
            break;
          case 'products':
            searchHook.searchProducts(query, setFilter);
            break;
          case 'cards':
            searchHook.searchCards(query, setFilter);
            break;
        }
        setIsOpen(true);
      },
      [searchType, setFilter, searchHook, minLength]
    );

    // Context7 Pattern: Optimized input change handler
    const handleInputChange = useCallback(
      (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value;
        setInputValue(value);
        setSelectedIndex(0);

        // Use startTransition for non-urgent updates
        startTransition(() => {
          onInputChange?.(value);
          triggerSearch(value);
        });
      },
      [onInputChange, triggerSearch]
    );

    // Context7 Pattern: Memoized selection handler
    const handleSelect = useCallback(
      (result: SearchResult) => {
        setInputValue(result.displayName);
        setIsOpen(false);
        onSelect(result);
      },
      [onSelect]
    );

    // Context7 Pattern: Memoized keyboard handler
    const handleKeyDown = useCallback(
      (e: React.KeyboardEvent) => {
        if (!isOpen || filteredResults.length === 0) {
          return;
        }

        switch (e.key) {
          case 'ArrowDown':
            e.preventDefault();
            setSelectedIndex((prev) => (prev + 1) % filteredResults.length);
            break;
          case 'ArrowUp':
            e.preventDefault();
            setSelectedIndex(
              (prev) =>
                (prev - 1 + filteredResults.length) % filteredResults.length
            );
            break;
          case 'Enter':
            e.preventDefault();
            if (filteredResults[selectedIndex]) {
              handleSelect(filteredResults[selectedIndex]);
            }
            break;
          case 'Escape':
            e.preventDefault();
            setIsOpen(false);
            inputRef.current?.blur();
            break;
        }
      },
      [isOpen, filteredResults, selectedIndex, handleSelect]
    );

    // Context7 Pattern: Effect with cleanup
    useEffect(() => {
      if (autoFocus && inputRef.current) {
        inputRef.current.focus();
      }
    }, [autoFocus]);

    // Context7 Pattern: Memoized suggestions list
    const suggestionsList = useMemo(() => {
      if (
        !isOpen ||
        (!searchHook.isSearching && filteredResults.length === 0)
      ) {
        return null;
      }

      return (
        <div className="absolute top-full left-0 right-0 z-50 mt-1 bg-zinc-900/95 backdrop-blur-xl border border-zinc-700/50 rounded-2xl shadow-2xl max-h-96 overflow-auto">
          <Suspense fallback={<SearchFallback />}>
            {searchHook.isSearching ? (
              <SearchFallback />
            ) : filteredResults.length > 0 ? (
              <div className="divide-y divide-zinc-700/30">
                {filteredResults.map((result, index) => (
                  <SuggestionItem
                    key={result._id || `${result.type}-${index}`}
                    result={result}
                    isSelected={selectedIndex === index}
                    onSelect={handleSelect}
                    searchQuery={inputValue}
                  />
                ))}
              </div>
            ) : (
              <div className="px-4 py-6 text-center text-zinc-400 text-sm">
                <Search className="w-6 h-6 mx-auto mb-2 text-zinc-500" />
                <div>No results found</div>
                <div className="text-xs mt-1 text-zinc-500">
                  Try different keywords
                </div>
              </div>
            )}
          </Suspense>
        </div>
      );
    }, [
      isOpen,
      searchHook.isSearching,
      filteredResults,
      selectedIndex,
      handleSelect,
      inputValue,
    ]);

    return (
      <div className={`relative ${className}`}>
        <div className="relative">
          <input
            ref={inputRef}
            type="text"
            value={inputValue}
            onChange={handleInputChange}
            onKeyDown={handleKeyDown}
            onFocus={() => {
              setIsFocused(true);
              if (inputValue.length >= minLength) {
                setIsOpen(true);
              }
            }}
            onBlur={() => {
              setIsFocused(false);
              // Delay closing to allow selection
              setTimeout(() => setIsOpen(false), 150);
            }}
            placeholder={placeholder}
            disabled={disabled}
            className={`
            w-full px-4 py-3 pr-10 bg-zinc-800/90 backdrop-blur-sm border ${elementTheme.border} rounded-2xl
            text-zinc-100 placeholder-zinc-400
            ${elementTheme.focus} shadow-lg hover:shadow-xl
            disabled:bg-zinc-800/50 disabled:cursor-not-allowed
            transition-all duration-300
            ${isFocused ? 'ring-2 ring-cyan-500/50 border-cyan-300' : ''}
          `}
          />
          <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
            {searchHook.isSearching ? (
              <Loader2 className="w-4 h-4 animate-spin text-cyan-400" />
            ) : (
              <Search className="w-4 h-4 text-zinc-400" />
            )}
          </div>
        </div>

        {suggestionsList}
      </div>
    );
  }
);

// Context7 Performance: Display name for debugging
OptimizedAutocomplete.displayName = 'OptimizedAutocomplete';

export default OptimizedAutocomplete;

/**
 * Add/Edit Sealed Product Form Component
 * Refactored to follow SOLID principles
 *
 * Following CLAUDE.md principles:
 * - Single Responsibility: Form orchestration only
 * - Dependency Inversion: Uses abstract hooks instead of concrete APIs
 * - Interface Segregation: Uses specialized hooks for specific concerns
 * - Open/Closed: Extensible through hook composition
 * - DRY: Reuses common form patterns
 */

import React, { useEffect, useState, useMemo } from 'react';
import { Archive, Camera, Package, TrendingUp } from 'lucide-react';
import { ISealedProduct } from '../../domain/models/sealedProduct';
import { useCollectionOperations } from '../../hooks/useCollectionOperations';
import { useBaseForm } from '../../hooks/useBaseForm';
import { commonValidationRules } from '../../hooks/useFormValidation';
import {
  useFormSubmission,
  FormSubmissionPatterns,
} from './wrappers/FormSubmissionWrapper';
import Input from '../common/Input';
import LoadingSpinner from '../common/LoadingSpinner';
import FormHeader from '../common/FormHeader';
import FormActionButtons from '../common/FormActionButtons';
import { ProductSearchSection } from './ProductSearchSection';
import ImageUploader from '../ImageUploader';
import { PriceHistoryDisplay } from '../PriceHistoryDisplay';
import ValidationField from './fields/ValidationField';
import { FormValidationService, VALIDATION_CONFIGS } from '../../services/forms/FormValidationService';
import {
  convertObjectIdToString,
  transformRequestData,
} from '../../utils/responseTransformer';

interface AddEditSealedProductFormProps {
  onCancel: () => void;
  onSuccess: () => void;
  initialData?: Partial<ISealedProduct>;
  isEditing?: boolean;
}

interface FormData {
  setName: string;
  productName: string;
  category: string;
  availability: number;
  cardMarketPrice: string;
  myPrice: string;
  dateAdded: string;
}

// Helper function to convert ISO date to YYYY-MM-DD format
const formatDateForInput = (isoDate: string | undefined): string => {
  if (!isoDate) {
    return new Date().toISOString().split('T')[0];
  }
  // Handle both ISO string and already formatted date strings
  if (isoDate.includes('T')) {
    return isoDate.split('T')[0];
  }
  return isoDate;
};

const AddEditSealedProductForm: React.FC<AddEditSealedProductFormProps> = ({
  onCancel,
  onSuccess,
  initialData,
  isEditing = false,
}) => {
  const { addSealedProduct, updateSealedProduct, loading } =
    useCollectionOperations();

  // Check if item is sold - prevent editing of sold items
  const isSold = isEditing && initialData?.sold;

  // Validation rules for Sealed Product form
  const validationRules = {
    setName: { required: true },
    productName: { required: true },
    category: { required: true },
    availability: {
      required: true,
      min: 0,
      custom: (value: string) => {
        const num = parseInt(value);
        if (isNaN(num)) {
          return 'Must be a valid number';
        }
        return undefined;
      },
    },
    cardMarketPrice: commonValidationRules.price,
    myPrice: { ...commonValidationRules.price, required: true },
  };

  // Memoize initialData to prevent infinite re-renders
  const memoizedInitialData = useMemo(() => {
    return initialData
      ? {
          setName: initialData.setName,
          productName: initialData.name, // Field mapping: name -> productName
          category: initialData.category,
          availability: initialData.availability,
          cardMarketPrice: initialData.cardMarketPrice?.toString(),
          myPrice: initialData.myPrice?.toString(),
          dateAdded: initialData.dateAdded,
        }
      : undefined;
  }, [initialData]);

  // Initialize base form with specialized hooks and standardized initialData handling
  const baseForm = useBaseForm<FormData>({
    defaultValues: {
      setName: initialData?.setName || '',
      productName: initialData?.name || '',
      category: initialData?.category || '',
      availability: initialData?.availability || 0,
      cardMarketPrice: initialData?.cardMarketPrice?.toString() || '',
      myPrice: initialData?.myPrice?.toString() || '',
      dateAdded: formatDateForInput(initialData?.dateAdded),
    },
    validationRules,
    initialImages: initialData?.images || [],
    initialPriceHistory: initialData?.priceHistory || [],
    initialPrice: initialData?.myPrice || 0,
    // Standardized initialData handling with memoization
    initialData: memoizedInitialData,
    isEditing,
    fieldMapping: {
      name: 'productName', // Map 'name' field to 'productName' form field
    },
  });

  const { form, isSubmitting, imageUpload, priceHistory, setSubmitting } =
    baseForm;
  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch,
    clearErrors,
  } = form;

  // State for product categories and selection
  const [productCategories, setProductCategories] = useState<
    Array<{ value: string; label: string }>
  >([]);
  const [loadingOptions, setLoadingOptions] = useState(true);
  const [selectedProductData, setSelectedProductData] = useState<Record<
    string,
    unknown
  > | null>(null);

  // Form-specific useEffect for unique internal state only (following CLAUDE.md SRP)
  useEffect(() => {
    if (isEditing && initialData) {
      // Form fields are now handled by useBaseForm centrally

      // Set selectedProductData for existing products to maintain reference link
      // This is form-specific logic that cannot be centralized
      if (initialData.productId) {
        // Use centralized ObjectId conversion
        const transformedInitialData = transformRequestData(initialData);

        setSelectedProductData({
          _id: transformedInitialData.productId,
          setName: initialData.setName,
          name: initialData.name,
          category: initialData.category,
          available: initialData.availability,
          price: initialData.cardMarketPrice,
        });
      }
    }
  }, [isEditing, initialData]);

  // Watch form fields for validation
  const watchedPrice = watch('myPrice');
  const watchedCardMarketPrice = watch('cardMarketPrice');

  // Load dynamic options from backend
  useEffect(() => {
    const loadOptions = async () => {
      setLoadingOptions(true);
      try {
        // Categories matching ACTUAL DATABASE VALUES
        const categories = [
          { value: 'Booster-Boxes', label: 'Booster Boxes' },
          { value: 'Elite-Trainer-Boxes', label: 'Elite Trainer Boxes' },
          { value: 'Box-Sets', label: 'Box Sets' },
          { value: 'Boosters', label: 'Boosters' },
        ];
        console.log(
          '[CATEGORIES DEBUG] Setting productCategories to:',
          categories
        );
        setProductCategories(categories);
      } catch (error) {
        console.error('Failed to load form options:', error);
      } finally {
        setLoadingOptions(false);
      }
    };

    loadOptions();
  }, []);

  // Update current price when form price changes
  useEffect(() => {
    if (watchedPrice) {
      const price = parseFloat(watchedPrice);
      if (!isNaN(price)) {
        priceHistory.updateCurrentPrice(price);
      }
    }
  }, [watchedPrice, priceHistory]);

  const handlePriceUpdate = (newPrice: number, _date: string) => {
    // Add new price to history using specialized hook
    priceHistory.addPriceEntry(newPrice, 'manual_update');

    // Update form field
    setValue('myPrice', newPrice.toString());
  };

  // Standardized submission handling using FormSubmissionWrapper
  const { handleSubmission } = useFormSubmission<
    FormData,
    Partial<ISealedProduct>
  >({
    setSubmitting,
    onSuccess,
    imageUpload,
    priceHistory,
    logContext: 'SEALED PRODUCT',
    validateBeforeSubmission: (_data) => {
      if (!selectedProductData?._id) {
        throw new Error(
          'Please select a product from the suggestions to ensure reference data link'
        );
      }
    },
    prepareSubmissionData: async ({ formData, imageUrls }) => {
      const allImageUrls = FormSubmissionPatterns.combineImages(
        imageUpload.remainingExistingImages,
        imageUrls
      );

      return {
        productId: selectedProductData?._id,
        setName: formData.setName.trim(),
        name: formData.productName.trim(),
        category: formData.category,
        availability: Number(formData.availability),
        cardMarketPrice: formData.cardMarketPrice
          ? parseFloat(formData.cardMarketPrice)
          : undefined,
        myPrice: parseFloat(formData.myPrice),
        dateAdded: formData.dateAdded,
        images: allImageUrls,
        priceHistory: FormSubmissionPatterns.transformPriceHistory(
          priceHistory.priceHistory,
          parseFloat(formData.myPrice)
        ),
      };
    },
    submitToApi: async (productData, isEditing) => {
      if (isEditing && initialData?.id) {
        const productId = convertObjectIdToString(initialData.id);
        await updateSealedProduct(productId, productData);
      } else {
        await addSealedProduct(productData);
      }
    },
  });

  // If trying to edit a sold item, show message and prevent editing
  if (isSold) {
    return (
      <div className="bg-red-500/10 backdrop-blur-xl border border-red-500/20 rounded-3xl p-8 text-center">
        <div className="w-16 h-16 bg-red-500/20 rounded-full flex items-center justify-center mx-auto mb-6">
          <Archive className="w-8 h-8 text-red-400" />
        </div>
        <h3 className="text-2xl font-bold text-red-400 mb-4">
          Item Already Sold
        </h3>
        <p className="text-red-300/80 mb-6 text-lg">
          This sealed product has been sold and cannot be edited. Sold items are
          locked to preserve transaction history.
        </p>
        <div className="bg-red-500/20 backdrop-blur-xl border border-red-500/30 rounded-2xl p-4 mb-6">
          <p className="text-red-300 font-semibold">
            Sale Price: {initialData?.saleDetails?.actualSoldPrice || 'N/A'} kr
          </p>
          <p className="text-red-300/80 text-sm mt-1">
            Sold on:{' '}
            {initialData?.saleDetails?.dateSold
              ? new Date(initialData.saleDetails.dateSold).toLocaleDateString()
              : 'N/A'}
          </p>
        </div>
        <button
          onClick={onCancel}
          className="bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 text-white px-8 py-3 rounded-2xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105 backdrop-blur-xl border border-red-500/20"
        >
          Back to Collection
        </button>
      </div>
    );
  }

  const onSubmit = (data: FormData) =>
    handleSubmission(data, { isEditing, itemId: initialData?.id });

  if (loading && !isSubmitting) {
    return (
      <div className="flex items-center justify-center py-12">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  // Calculate potential profit margin
  const calculateProfitMargin = () => {
    const myPrice = parseFloat(watchedPrice || '0');
    const marketPrice = parseFloat(watchedCardMarketPrice || '0');
    if (myPrice > 0 && marketPrice > 0) {
      const margin = ((marketPrice - myPrice) / myPrice) * 100;
      return margin.toFixed(1);
    }
    return null;
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="space-y-8">
      {/* Reusable Form Header */}
      <FormHeader
        icon={Archive}
        title={isEditing ? 'Edit Sealed Product' : 'Add Sealed Product'}
        description={
          isEditing
            ? 'Update your sealed product information'
            : 'Add a new sealed product to your premium collection'
        }
        isEditing={isEditing}
        primaryColorClass="purple"
      />

      {/* Item Status Indicator for editing mode */}
      {isEditing && initialData && (
        <div className="bg-zinc-900/80 backdrop-blur-xl border border-zinc-700/20 rounded-3xl p-6 shadow-2xl">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="p-2 rounded-xl bg-gradient-to-br from-blue-500/20 to-indigo-600/20 backdrop-blur-xl border border-white/10 shadow-lg">
                <Archive className="w-5 h-5 text-blue-400" />
              </div>
              <div>
                <h4 className="text-lg font-bold text-zinc-100">Item Status</h4>
                <p className="text-zinc-400 text-sm">
                  Current availability status
                </p>
              </div>
            </div>
            <div
              className={`px-4 py-2 rounded-xl backdrop-blur-xl border font-semibold ${
                initialData.sold
                  ? 'bg-red-500/20 border-red-500/30 text-red-300'
                  : 'bg-emerald-500/20 border-emerald-500/30 text-emerald-300'
              }`}
            >
              {initialData.sold ? 'SOLD' : 'AVAILABLE'}
            </div>
          </div>

          {initialData.sold && initialData.saleDetails && (
            <div className="mt-4 pt-4 border-t border-zinc-700/50">
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="text-zinc-400">Sale Price:</span>
                  <span className="ml-2 font-semibold text-green-400">
                    {initialData.saleDetails.actualSoldPrice || 'N/A'} kr
                  </span>
                </div>
                <div>
                  <span className="text-zinc-400">Date Sold:</span>
                  <span className="ml-2 font-semibold text-zinc-300">
                    {initialData.saleDetails.dateSold
                      ? new Date(
                          initialData.saleDetails.dateSold
                        ).toLocaleDateString()
                      : 'N/A'}
                  </span>
                </div>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Product Search Section - Hidden in edit mode like other forms */}
      {!isEditing && (
        <div className="mb-6 relative z-10">
          <ProductSearchSection
            register={register}
            errors={errors}
            setValue={setValue}
            watch={watch}
            clearErrors={clearErrors}
            onSelectionChange={(selectedData) => {
              console.log('[SEALED PRODUCT] Product selection:', selectedData);

              // Store selected product data for form submission (CRITICAL - maintains existing behavior)
              setSelectedProductData(selectedData);
            }}
            onError={(error) => {
              console.error('[SEALED PRODUCT] Search error:', error);
            }}
            sectionTitle="Product Information"
            sectionIcon={Package}
            formType="product"
            readOnlyFields={{
              category: true,
              availability: true,
            }}
            productCategories={productCategories}
            loadingOptions={loadingOptions}
          />
        </div>
      )}

      {/* Context7 Premium Pricing & Investment Section */}
      <div className="bg-zinc-900/80 backdrop-blur-xl border border-zinc-700/20 rounded-3xl p-8 shadow-2xl relative">
        <div className="absolute inset-0 bg-gradient-to-br from-zinc-800/50 to-purple-900/50 pointer-events-none"></div>

        <h4 className="text-xl font-bold text-zinc-100 mb-6 flex items-center relative">
          <TrendingUp className="w-6 h-6 mr-3 text-zinc-300" />
          Pricing & Investment Metrics
        </h4>

        {/* Auto-filled fields: CardMarket Price (from product selection) */}
        {/* User-editable fields: My Price, Date Added */}

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 relative">
          <div>
            <Input
              label="CardMarket Price (kr.)"
              type="number"
              step="1"
              min="0"
              {...register('cardMarketPrice', {
                min: { value: 0, message: 'Price must be non-negative' },
                pattern: {
                  value: /^\d+$/,
                  message: 'Price must be a whole number',
                },
              })}
              error={errors.cardMarketPrice?.message}
              placeholder="0"
              disabled={true} // Always disabled - autofilled from product selection
            />
            {watchedCardMarketPrice && (
              <div className="mt-2 text-sm text-purple-600 font-semibold">
                Market: {parseFloat(watchedCardMarketPrice || '0')} kr.
              </div>
            )}
            {selectedProductData && selectedProductData.price && (
              <div className="mt-2 text-sm text-green-600 font-semibold">
                CardMarket Price:{' '}
                {Math.round(parseFloat(selectedProductData.price))} kr.
              </div>
            )}
          </div>

          <div>
            <ValidationField
              name="myPrice"
              label="My Price (kr.)"
              type="price"
              placeholder="0"
              required={true}
              register={register}
              error={errors.myPrice}
            />
            {watchedPrice && (
              <div className="mt-2 text-sm text-slate-600 dark:text-zinc-400 dark:text-zinc-300 font-semibold">
                Paid: {parseFloat(watchedPrice || '0')} kr.
              </div>
            )}
          </div>

          <div>
            <ValidationField
              name="dateAdded"
              label="Date Added"
              type="date"
              required={true}
              disabled={isEditing}
              register={register}
              error={errors.dateAdded}
            />

            {/* Investment Analysis */}
            {calculateProfitMargin() && (
              <div className="mt-3 p-3 bg-gradient-to-r from-green-50/80 to-emerald-50/80 border border-green-200/50 rounded-xl backdrop-blur-sm">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-bold text-green-800">
                    Potential Margin:
                  </span>
                  <span
                    className={`text-sm font-bold ${parseFloat(calculateProfitMargin()!) > 0 ? 'text-green-600' : 'text-red-600'}`}
                  >
                    {calculateProfitMargin()}%
                  </span>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Price History Section (for editing existing products) */}
        {isEditing && priceHistory.priceHistory.length > 0 && (
          <div className="mt-8 pt-8 border-t border-slate-200/50 dark:border-zinc-700/50 dark:border-zinc-700/50">
            <PriceHistoryDisplay
              priceHistory={priceHistory.priceHistory}
              currentPrice={priceHistory.currentPrice}
              onPriceUpdate={handlePriceUpdate}
            />
          </div>
        )}
      </div>

      {/* Context7 Premium Image Upload Section */}
      <div className="bg-zinc-900/80 backdrop-blur-xl border border-zinc-700/20 rounded-3xl p-8 shadow-2xl relative">
        <div className="absolute inset-0 bg-gradient-to-br from-zinc-800/50 to-zinc-900/50 pointer-events-none"></div>

        <h4 className="text-xl font-bold text-zinc-100 mb-6 flex items-center relative">
          <Camera className="w-6 h-6 mr-3 text-zinc-300" />
          Product Images
        </h4>

        <div className="relative">
          <ImageUploader
            onImagesChange={imageUpload.handleImagesChange}
            existingImageUrls={imageUpload.remainingExistingImages}
            multiple={true}
            maxFiles={8}
            maxFileSize={5}
          />

          <div className="mt-4 p-4 bg-gradient-to-r from-slate-50/80 to-purple-50/80 rounded-xl border border-slate-200/50 dark:border-zinc-700/50 dark:border-zinc-700/50 backdrop-blur-sm">
            <div className="text-sm text-slate-600 dark:text-zinc-400 dark:text-zinc-300 space-y-1">
              <p className="font-semibold">
                 Upload up to 8 images (max 5MB each)
              </p>
              <p> Supported formats: JPG, PNG, WebP</p>
              <p>
                 Include package front, back, and seal shots for authenticity
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Reusable Action Buttons */}
      <FormActionButtons
        onCancel={onCancel}
        isSubmitting={isSubmitting}
        isEditing={isEditing}
        submitButtonText={isEditing ? 'Update Product' : 'Add Product'}
        loadingSubmitText={isEditing ? 'Updating...' : 'Adding...'}
        primaryButtonColorClass="purple"
      />
    </form>
  );
};

export default AddEditSealedProductForm;

/**
 * SearchField Component
 * Layer 3: Components (UI Building Blocks)
 * 
 * Following CLAUDE.md SOLID principles:
 * - Single Responsibility: Handles autocomplete search with hierarchical filtering
 * - DRY: Eliminates duplication between CardSearchSection + ProductSearchSection
 * - Interface Segregation: Focused interface for search functionality
 * - Open/Closed: Extensible search types without modification
 * - NOT over-engineered: Simple, focused search field component
 */

import React, { useEffect, useState, useCallback } from 'react';
import { LucideIcon, Search } from 'lucide-react';
import { FieldError } from 'react-hook-form';
import { SearchResult, useSearch } from '../../../hooks/useSearch';
import { useDebouncedValue } from '../../../hooks/useDebounce';

export type SearchFieldType = 'card' | 'product';

interface SearchFieldProps {
  /** Field configuration */
  name: string;
  label: string;
  searchType: SearchFieldType;
  placeholder?: string;
  required?: boolean;
  disabled?: boolean;

  /** Form integration */
  value: string;
  onChange: (value: string) => void;
  error?: FieldError;

  /** Hierarchical search */
  parentFieldValue?: string; // For filtering (e.g., setName for card search)

  /** Selection callback */
  onSelection: (result: SearchResult | null) => void;

  /** Styling */
  icon?: LucideIcon;
  className?: string;
}

/**
 * SearchField Component
 * Handles both 'card' and 'product' search types with hierarchical filtering
 * Maintains current search functionality while eliminating duplication
 */
export const SearchField: React.FC<SearchFieldProps> = ({
  name,
  label,
  searchType,
  placeholder,
  required = false,
  disabled = false,
  value,
  onChange,
  error,
  parentFieldValue,
  onSelection,
  icon: Icon = Search,
  className,
}) => {
  // State management
  const [isActive, setIsActive] = useState(false);
  const [suggestions, setSuggestions] = useState<SearchResult[]>([]);

  // Use centralized search hook
  const search = useSearch();

  // Debounce search query
  const debouncedValue = useDebouncedValue(value, 300);

  // Sync search results to local suggestions state
  useEffect(() => {
    setSuggestions(search.results || []);
  }, [search.results]);

  // Centralized search effect
  useEffect(() => {
    if (!isActive) {
      setSuggestions([]);
      return;
    }

    if (!debouncedValue || typeof debouncedValue !== 'string') {
      setSuggestions([]);
      return;
    }

    // Execute search based on type
    switch (searchType) {
      case 'card':
        // Smart search: If no query but set selected, show all from set
        let cardSearchQuery = debouncedValue;
        if (!debouncedValue.trim()) {
          if (parentFieldValue?.trim()) {
            cardSearchQuery = '*';
          } else {
            setSuggestions([]);
            return;
          }
        }
        search.searchCards(cardSearchQuery, parentFieldValue?.trim() || undefined);
        break;

      case 'product':
        // Smart search: If no query but set selected, show all from set
        let productSearchQuery = debouncedValue;
        if (!debouncedValue.trim()) {
          if (parentFieldValue?.trim()) {
            productSearchQuery = '*';
          } else {
            setSuggestions([]);
            return;
          }
        }
        search.searchProducts(productSearchQuery, parentFieldValue?.trim() || undefined);
        break;
    }
  }, [isActive, debouncedValue, parentFieldValue, searchType, search]);

  // Handle selection
  const handleSelection = useCallback(
    (result: SearchResult) => {
      if (!result.id || !result.displayName) {
        onChange('');
        onSelection(null);
        setSuggestions([]);
        setIsActive(false);
        return;
      }

      // Update field value
      onChange(result.displayName);

      // Call selection callback
      onSelection({
        ...result,
        data: {
          _id: result.id,
          ...result.data,
        },
      });

      // Clear suggestions
      setTimeout(() => {
        setSuggestions([]);
        setIsActive(false);
      }, 10);
    },
    [onChange, onSelection]
  );

  // Handle focus with auto-trigger for hierarchical search
  const handleFocus = useCallback(() => {
    setIsActive(true);

    // Auto-trigger search if parent field is selected but current field is empty
    if (
      parentFieldValue &&
      parentFieldValue.trim() &&
      (!value || value.trim() === '')
    ) {
      // Trigger search with wildcard to show all items from parent
      switch (searchType) {
        case 'card':
          search.searchCards('*', parentFieldValue.trim());
          break;
        case 'product':
          search.searchProducts('*', parentFieldValue.trim());
          break;
      }
    }
  }, [parentFieldValue, value, searchType, search]);

  // Handle blur
  const handleBlur = useCallback(() => {
    setTimeout(() => {
      setIsActive(false);
    }, 150);
  }, []);

  return (
    <div className="relative z-50">
      <label className="block text-sm font-bold text-white mb-3 flex items-center space-x-2">
        <div className="p-1 rounded-lg bg-gradient-to-br from-blue-500/20 to-purple-600/20 backdrop-blur-xl border border-white/10">
          <Icon className="w-3 h-3 text-blue-400" />
        </div>
        <span>{label}</span>
        {required && <span className="text-red-400 ml-1 font-bold">*</span>}
      </label>

      <div className="relative">
        {/* Background Glass Effects */}
        <div className="absolute -inset-1 bg-gradient-to-r from-blue-500/10 via-purple-500/10 to-indigo-500/10 rounded-[1.2rem] blur-md opacity-60"></div>

        <div className="relative bg-black/40 backdrop-blur-3xl rounded-xl shadow-xl border border-white/10 ring-1 ring-white/5 overflow-hidden group">
          {/* Floating Orbs */}
          <div className="absolute -top-1 -right-1 w-12 h-12 bg-gradient-to-br from-blue-500/10 to-purple-500/10 rounded-full blur-lg animate-pulse opacity-0 group-focus-within:opacity-100 transition-opacity duration-500"></div>

          <input
            type="text"
            value={value}
            onChange={(e) => onChange(e.target.value)}
            onFocus={handleFocus}
            onBlur={handleBlur}
            placeholder={placeholder || `Search for ${label.toLowerCase()}...`}
            disabled={disabled}
            className={`relative z-10 block w-full px-4 py-3 bg-transparent border-none text-white placeholder-white/50 font-medium focus:ring-0 focus:outline-none ${className || ''}`}
          />

          {/* Shimmer Effect */}
          <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent -translate-x-full group-focus-within:translate-x-full transition-transform duration-1000 ease-out pointer-events-none"></div>

          {/* Breathing Animation */}
          <div className="absolute inset-0 bg-gradient-to-r from-blue-500/5 via-purple-500/5 to-indigo-500/5 rounded-xl animate-pulse opacity-40 pointer-events-none group-focus-within:opacity-60 transition-opacity duration-300"></div>
        </div>

        {/* Suggestions Dropdown */}
        {isActive && suggestions.length > 0 && (
          <div className="absolute top-full left-0 right-0 z-[9999] mt-2">
            <div className="relative bg-zinc-900 rounded-2xl shadow-2xl border border-white/10 ring-1 ring-white/5 overflow-hidden max-h-80">
              <div className="p-2 space-y-1 max-h-80 overflow-y-auto scrollbar-thin scrollbar-thumb-blue-500/30 scrollbar-track-transparent relative z-10">
                {suggestions.map((suggestion, index) => (
                  <div
                    key={suggestion.id || `${searchType}-suggestion-${index}`}
                    onClick={() => handleSelection(suggestion)}
                    className="group cursor-pointer select-none relative p-4 rounded-xl transition-all duration-300 transform hover:scale-102 overflow-hidden hover:bg-white/5 border border-transparent hover:border-white/10"
                    role="option"
                  >
                    <div className="relative z-10 flex items-center justify-between">
                      <div className="flex-1 min-w-0 space-y-1">
                        <div className="text-white font-bold text-lg leading-tight truncate group-hover:text-blue-400 transition-colors duration-300">
                          {suggestion.displayName}
                        </div>

                        {/* Metadata */}
                        <div className="flex flex-wrap items-center gap-2">
                          {parentFieldValue && parentFieldValue.trim() && (
                            <div className="inline-flex items-center px-2 py-1 bg-gradient-to-r from-emerald-500/20 to-teal-600/20 backdrop-blur-xl border border-emerald-500/30 rounded-lg text-xs text-emerald-300 font-semibold">
                              Set: {parentFieldValue}
                            </div>
                          )}
                          {suggestion.data?.cardNumber && (
                            <div className="inline-flex items-center px-2 py-1 bg-gradient-to-r from-purple-500/20 to-violet-600/20 backdrop-blur-xl border border-purple-500/30 rounded-lg text-xs text-purple-300 font-semibold">
                              #{suggestion.data.cardNumber}
                            </div>
                          )}
                          {suggestion.data?.category && (
                            <div className="inline-flex items-center px-2 py-1 bg-gradient-to-r from-purple-500/20 to-violet-600/20 backdrop-blur-xl border border-purple-500/30 rounded-lg text-xs text-purple-300 font-semibold">
                              {suggestion.data.category}
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* No Results */}
        {isActive &&
          value.trim().length >= 2 &&
          suggestions.length === 0 &&
          !search.isLoading && (
            <div className="absolute top-full left-0 right-0 z-[9999] mt-2">
              <div className="relative bg-zinc-900 rounded-2xl shadow-2xl border border-white/10 ring-1 ring-white/5 overflow-hidden">
                <div className="p-8 text-center relative z-10">
                  <div className="relative mb-6">
                    <div className="w-16 h-16 bg-gradient-to-br from-slate-800/60 to-slate-900/80 rounded-2xl flex items-center justify-center mx-auto border border-white/10 shadow-lg">
                      <Search className="w-8 h-8 text-white/60" />
                    </div>
                  </div>
                  <h3 className="text-lg font-bold text-white mb-2">
                    No results found
                  </h3>
                  <p className="text-white/60 text-sm font-medium">
                    Try adjusting your search terms
                  </p>
                </div>
              </div>
            </div>
          )}
      </div>

      {/* Error Message */}
      {error && (
        <div className="mt-3 p-3 bg-gradient-to-r from-red-500/20 to-pink-600/20 backdrop-blur-xl border border-red-500/30 rounded-xl shadow-lg flex items-center space-x-2">
          <div className="p-1 rounded-lg bg-gradient-to-br from-red-500/20 to-pink-600/20 backdrop-blur-xl border border-white/10">
            <Icon className="w-3 h-3 text-red-400" />
          </div>
          <p className="text-sm text-red-300 font-medium">
            {error.message}
          </p>
        </div>
      )}
    </div>
  );
};

export default SearchField;

