/**
 * Accessibility Theme Provider
 * AGENT 3: THEMECONTEXT DECOMPOSITION - Task 1.4
 * 
 * Focused context for accessibility settings management following ISP
 * Handles: high contrast, reduced motion, and a11y-related theme aspects
 */

import React, { createContext, useContext, useCallback } from 'react';

// ================================
// ACCESSIBILITY THEME INTERFACES
// ================================

export interface AccessibilityThemeState {
  highContrast: boolean;
  reducedMotion: boolean;
}

export interface AccessibilityThemeContextType {
  // Current State
  highContrast: boolean;
  reducedMotion: boolean;

  // Accessibility Management
  toggleHighContrast: () => void;
  toggleReducedMotion: () => void;
  setHighContrast: (enabled: boolean) => void;
  setReducedMotion: (enabled: boolean) => void;

  // Utility Functions
  getAccessibilityClasses: () => string;
  shouldReduceMotion: () => boolean;
  isHighContrastEnabled: () => boolean;
}

// ================================
// CONTEXT SETUP
// ================================

const AccessibilityThemeContext = createContext<AccessibilityThemeContextType | null>(null);

export interface AccessibilityThemeProviderProps {
  children: React.ReactNode;
  state: AccessibilityThemeState;
  onStateChange: (newState: Partial<AccessibilityThemeState>) => void;
}

/**
 * Accessibility Theme Provider Component
 * Manages accessibility state following Single Responsibility Principle
 * Only handles accessibility-related theme aspects (contrast, motion, etc.)
 */
export const AccessibilityThemeProvider: React.FC<AccessibilityThemeProviderProps> = ({
  children,
  state,
  onStateChange,
}) => {
  // Accessibility manipulation functions
  const toggleHighContrast = useCallback(() => {
    onStateChange({ highContrast: !state.highContrast });
  }, [onStateChange, state.highContrast]);

  const toggleReducedMotion = useCallback(() => {
    onStateChange({ reducedMotion: !state.reducedMotion });
  }, [onStateChange, state.reducedMotion]);

  const setHighContrast = useCallback((enabled: boolean) => {
    onStateChange({ highContrast: enabled });
  }, [onStateChange]);

  const setReducedMotion = useCallback((enabled: boolean) => {
    onStateChange({ reducedMotion: enabled });
  }, [onStateChange]);

  // Utility functions
  const getAccessibilityClasses = useCallback((): string => {
    const classes: string[] = [];

    if (state.highContrast) {
      classes.push('high-contrast');
    }
    if (state.reducedMotion) {
      classes.push('reduced-motion');
    }

    return classes.join(' ');
  }, [state]);

  const shouldReduceMotion = useCallback((): boolean => {
    return state.reducedMotion;
  }, [state.reducedMotion]);

  const isHighContrastEnabled = useCallback((): boolean => {
    return state.highContrast;
  }, [state.highContrast]);

  const contextValue: AccessibilityThemeContextType = {
    // Current State
    highContrast: state.highContrast,
    reducedMotion: state.reducedMotion,

    // Accessibility Management
    toggleHighContrast,
    toggleReducedMotion,
    setHighContrast,
    setReducedMotion,

    // Utility Functions
    getAccessibilityClasses,
    shouldReduceMotion,
    isHighContrastEnabled,
  };

  return (
    <AccessibilityThemeContext.Provider value={contextValue}>
      {children}
    </AccessibilityThemeContext.Provider>
  );
};

/**
 * Hook to access accessibility theme context
 * Provides type-safe access to accessibility theme functionality
 */
export const useAccessibilityTheme = (): AccessibilityThemeContextType => {
  const context = useContext(AccessibilityThemeContext);
  if (!context) {
    throw new Error('useAccessibilityTheme must be used within an AccessibilityThemeProvider');
  }
  return context;
};

/**
 * Animation Theme Provider
 * AGENT 3: THEMECONTEXT DECOMPOSITION - Task 1.3
 * 
 * Focused context for animation and motion management following ISP
 * Handles: animation intensity, durations, delays, and motion settings
 */

import React, { createContext, useContext, useCallback } from 'react';
import { AnimationIntensity } from '../ThemeContext';

// ================================
// ANIMATION THEME INTERFACES
// ================================

export interface AnimationThemeState {
  animationIntensity: AnimationIntensity;
}

export interface AnimationDurations {
  fast: string;
  normal: string;
  slow: string;
}

export interface AnimationDelays {
  short: string;
  medium: string;
  long: string;
}

export interface ComplexAnimationDurations {
  orbit: string;
  particle: string;
}

export interface AnimationThemeContextType {
  // Current State
  animationIntensity: AnimationIntensity;

  // Animation Management
  setAnimationIntensity: (intensity: AnimationIntensity) => void;

  // Utility Functions
  getAnimationDurations: () => AnimationDurations;
  getAnimationDelays: () => AnimationDelays;
  getComplexAnimationDurations: () => ComplexAnimationDurations;
  getAnimationClasses: () => string;
  isAnimationDisabled: () => boolean;
}

// ================================
// ANIMATION UTILITIES
// ================================

const animationDurationMap: Record<AnimationIntensity, AnimationDurations> = {
  subtle: { fast: '0.1s', normal: '0.2s', slow: '0.3s' },
  normal: { fast: '0.15s', normal: '0.3s', slow: '0.5s' },
  enhanced: { fast: '0.2s', normal: '0.4s', slow: '0.7s' },
  disabled: { fast: '0s', normal: '0s', slow: '0s' },
};

const animationDelays: AnimationDelays = {
  short: '0.2s',
  medium: '0.5s',
  long: '0.9s',
};

const complexAnimationDurations: ComplexAnimationDurations = {
  orbit: '15s',
  particle: '20s',
};

// ================================
// CONTEXT SETUP
// ================================

const AnimationThemeContext = createContext<AnimationThemeContextType | null>(null);

export interface AnimationThemeProviderProps {
  children: React.ReactNode;
  state: AnimationThemeState;
  onStateChange: (newState: Partial<AnimationThemeState>) => void;
}

/**
 * Animation Theme Provider Component
 * Manages animation and motion state following Single Responsibility Principle
 * Only handles animation intensity, durations, and motion-related theme aspects
 */
export const AnimationThemeProvider: React.FC<AnimationThemeProviderProps> = ({
  children,
  state,
  onStateChange,
}) => {
  // Animation manipulation functions
  const setAnimationIntensity = useCallback((intensity: AnimationIntensity) => {
    onStateChange({ animationIntensity: intensity });
  }, [onStateChange]);

  // Utility functions
  const getAnimationDurations = useCallback((): AnimationDurations => {
    return animationDurationMap[state.animationIntensity];
  }, [state.animationIntensity]);

  const getAnimationDelays = useCallback((): AnimationDelays => {
    return animationDelays;
  }, []);

  const getComplexAnimationDurations = useCallback((): ComplexAnimationDurations => {
    return complexAnimationDurations;
  }, []);

  const getAnimationClasses = useCallback((): string => {
    return `animation-${state.animationIntensity}`;
  }, [state.animationIntensity]);

  const isAnimationDisabled = useCallback((): boolean => {
    return state.animationIntensity === 'disabled';
  }, [state.animationIntensity]);

  const contextValue: AnimationThemeContextType = {
    // Current State
    animationIntensity: state.animationIntensity,

    // Animation Management
    setAnimationIntensity,

    // Utility Functions
    getAnimationDurations,
    getAnimationDelays,
    getComplexAnimationDurations,
    getAnimationClasses,
    isAnimationDisabled,
  };

  return (
    <AnimationThemeContext.Provider value={contextValue}>
      {children}
    </AnimationThemeContext.Provider>
  );
};

/**
 * Hook to access animation theme context
 * Provides type-safe access to animation theme functionality
 */
export const useAnimationTheme = (): AnimationThemeContextType => {
  const context = useContext(AnimationThemeContext);
  if (!context) {
    throw new Error('useAnimationTheme must be used within an AnimationThemeProvider');
  }
  return context;
};

/**
 * Composed Theme Provider
 * AGENT 3: THEMECONTEXT DECOMPOSITION - Task 4
 * 
 * Composes all focused theme providers following DIP
 * Maintains existing theme functionality while enabling focused usage
 * Acts as the orchestrator for all theme-related state management
 */

import React, { useState, useEffect, useCallback } from 'react';
import { useTheme as useNextTheme } from 'next-themes';
import { 
  ThemeConfiguration, 
  defaultConfig as originalDefaultConfig,
  ColorScheme,
  ThemeColor 
} from '../ThemeContext';
import { VisualThemeProvider, VisualThemeState } from './VisualThemeProvider';
import { LayoutThemeProvider, LayoutThemeState } from './LayoutThemeProvider';
import { AnimationThemeProvider, AnimationThemeState } from './AnimationThemeProvider';
import { AccessibilityThemeProvider, AccessibilityThemeState } from './AccessibilityThemeProvider';
import { ThemeStorageProvider } from './ThemeStorageProvider';
import { formThemes } from '../../theme/formThemes';

// ================================
// COMPOSED THEME STATE
// ================================

interface ComposedThemeState {
  visual: VisualThemeState;
  layout: LayoutThemeState;
  animation: AnimationThemeState;
  accessibility: AccessibilityThemeState;
  colorScheme: ColorScheme;
  primaryColor: ThemeColor;
  customCSSProperties?: Record<string, string>;
  isThemeLoaded: boolean;
}

// ================================
// DEFAULT CONFIGURATION
// ================================

const defaultConfig: ComposedThemeState = {
  visual: {
    visualTheme: 'context7-premium',
    glassmorphismIntensity: 80,
    particleEffectsEnabled: true,
  },
  layout: {
    density: 'comfortable',
  },
  animation: {
    animationIntensity: 'normal',
  },
  accessibility: {
    highContrast: false,
    reducedMotion: false,
  },
  colorScheme: 'system',
  primaryColor: 'dark',
  isThemeLoaded: false,
};

// ================================
// STORAGE UTILITIES
// ================================

const STORAGE_KEY = 'pokemon-theme-config';

const saveComposedConfig = (config: ComposedThemeState): void => {
  try {
    // Convert to the original ThemeConfiguration format for storage compatibility
    const legacyConfig: ThemeConfiguration = {
      visualTheme: config.visual.visualTheme,
      colorScheme: config.colorScheme,
      density: config.layout.density,
      animationIntensity: config.animation.animationIntensity,
      primaryColor: config.primaryColor,
      highContrast: config.accessibility.highContrast,
      reducedMotion: config.accessibility.reducedMotion,
      glassmorphismIntensity: config.visual.glassmorphismIntensity,
      particleEffectsEnabled: config.visual.particleEffectsEnabled,
      customCSSProperties: config.customCSSProperties,
    };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(legacyConfig));
  } catch (error) {
    console.warn('Failed to save theme configuration:', error);
  }
};

const loadComposedConfig = (): ComposedThemeState => {
  try {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      const legacyConfig: ThemeConfiguration = JSON.parse(stored);
      return {
        visual: {
          visualTheme: legacyConfig.visualTheme,
          glassmorphismIntensity: legacyConfig.glassmorphismIntensity,
          particleEffectsEnabled: legacyConfig.particleEffectsEnabled,
        },
        layout: {
          density: legacyConfig.density,
        },
        animation: {
          animationIntensity: legacyConfig.animationIntensity,
        },
        accessibility: {
          highContrast: legacyConfig.highContrast,
          reducedMotion: legacyConfig.reducedMotion,
        },
        colorScheme: legacyConfig.colorScheme,
        primaryColor: legacyConfig.primaryColor,
        customCSSProperties: legacyConfig.customCSSProperties,
        isThemeLoaded: true,
      };
    }
  } catch (error) {
    console.warn('Failed to load theme configuration:', error);
  }
  return { ...defaultConfig, isThemeLoaded: true };
};

// ================================
// COMPOSED THEME PROVIDER
// ================================

export interface ComposedThemeProviderProps {
  children: React.ReactNode;
}

/**
 * Composed Theme Provider Component
 * Orchestrates all focused theme providers and manages global theme state
 * Maintains backward compatibility while enabling focused theme usage
 */
export const ComposedThemeProvider: React.FC<ComposedThemeProviderProps> = ({
  children,
}) => {
  const { setTheme: setNextTheme } = useNextTheme();
  const [config, setConfig] = useState<ComposedThemeState>(defaultConfig);

  // Load configuration on mount
  useEffect(() => {
    const loadedConfig = loadComposedConfig();
    setConfig(loadedConfig);

    // Sync color scheme with next-themes
    if (loadedConfig.colorScheme !== 'system') {
      setNextTheme(loadedConfig.colorScheme);
    }

    // Auto dark/light mode detection for system theme
    if (loadedConfig.colorScheme === 'system' && typeof window !== 'undefined') {
      const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');

      const handleSystemThemeChange = () => {
        setConfig((prev) => {
          if (prev.colorScheme === 'system') {
            setNextTheme('system');
            return prev;
          }
          return prev;
        });
      };

      if (mediaQuery.addEventListener) {
        mediaQuery.addEventListener('change', handleSystemThemeChange);
        return () => mediaQuery.removeEventListener('change', handleSystemThemeChange);
      } else {
        mediaQuery.addListener(handleSystemThemeChange);
        return () => mediaQuery.removeListener(handleSystemThemeChange);
      }
    }
  }, [setNextTheme]);

  // Save configuration and update CSS whenever it changes
  useEffect(() => {
    if (config.isThemeLoaded) {
      saveComposedConfig(config);
      updateCSSProperties(config);
    }
  }, [config]);

  // Update CSS custom properties based on theme configuration
  const updateCSSProperties = useCallback((themeConfig: ComposedThemeState) => {
    if (typeof document === 'undefined') return;

    const root = document.documentElement;
    const formTheme = formThemes[themeConfig.primaryColor];

    // Density tokens
    const densityMultiplier = {
      compact: 0.8,
      comfortable: 1,
      spacious: 1.2,
    }[themeConfig.layout.density];

    // Animation tokens
    const animationDurations = {
      subtle: { fast: '0.1s', normal: '0.2s', slow: '0.3s' },
      normal: { fast: '0.15s', normal: '0.3s', slow: '0.5s' },
      enhanced: { fast: '0.2s', normal: '0.4s', slow: '0.7s' },
      disabled: { fast: '0s', normal: '0s', slow: '0s' },
    }[themeConfig.animation.animationIntensity];

    // Apply theme tokens
    root.style.setProperty('--theme-primary-gradient', formTheme.button.primary);
    root.style.setProperty('--theme-primary-hover', formTheme.button.primaryHover);
    root.style.setProperty('--theme-header-background', formTheme.header.background);
    root.style.setProperty('--theme-border-color', formTheme.element.border);
    root.style.setProperty('--theme-focus-ring', formTheme.element.focus);

    // Density spacing
    root.style.setProperty('--density-spacing-xs', `${0.25 * densityMultiplier}rem`);
    root.style.setProperty('--density-spacing-sm', `${0.5 * densityMultiplier}rem`);
    root.style.setProperty('--density-spacing-md', `${1 * densityMultiplier}rem`);
    root.style.setProperty('--density-spacing-lg', `${1.5 * densityMultiplier}rem`);
    root.style.setProperty('--density-spacing-xl', `${2 * densityMultiplier}rem`);

    // Animation durations
    root.style.setProperty('--animation-duration-fast', animationDurations.fast);
    root.style.setProperty('--animation-duration-normal', animationDurations.normal);
    root.style.setProperty('--animation-duration-slow', animationDurations.slow);

    // Animation delays for orchestrated effects
    root.style.setProperty('--animation-delay-short', '0.2s');
    root.style.setProperty('--animation-delay-medium', '0.5s');
    root.style.setProperty('--animation-delay-long', '0.9s');

    // Complex animation durations for special effects
    root.style.setProperty('--animation-duration-orbit', '15s');
    root.style.setProperty('--animation-duration-particle', '20s');

    // Glassmorphism intensity
    const glassAlpha = themeConfig.visual.glassmorphismIntensity / 100;
    root.style.setProperty('--glass-alpha', glassAlpha.toString());
    root.style.setProperty('--glass-blur', `${themeConfig.visual.glassmorphismIntensity / 5}px`);

    // Visual theme classes
    root.className = root.className.replace(/theme-\w+/g, '');
    root.classList.add(`theme-${themeConfig.visual.visualTheme}`);

    // Accessibility settings
    if (themeConfig.accessibility.reducedMotion) {
      root.style.setProperty('--animation-duration-fast', '0s');
      root.style.setProperty('--animation-duration-normal', '0s');
      root.style.setProperty('--animation-duration-slow', '0s');
    }

    // Custom properties
    if (themeConfig.customCSSProperties) {
      Object.entries(themeConfig.customCSSProperties).forEach(([key, value]) => {
        root.style.setProperty(key, value);
      });
    }
  }, []);

  // State change handlers for focused providers
  const handleVisualStateChange = useCallback((newVisualState: Partial<VisualThemeState>) => {
    setConfig(prev => ({
      ...prev,
      visual: { ...prev.visual, ...newVisualState }
    }));
  }, []);

  const handleLayoutStateChange = useCallback((newLayoutState: Partial<LayoutThemeState>) => {
    setConfig(prev => ({
      ...prev,
      layout: { ...prev.layout, ...newLayoutState }
    }));
  }, []);

  const handleAnimationStateChange = useCallback((newAnimationState: Partial<AnimationThemeState>) => {
    setConfig(prev => ({
      ...prev,
      animation: { ...prev.animation, ...newAnimationState }
    }));
  }, []);

  const handleAccessibilityStateChange = useCallback((newAccessibilityState: Partial<AccessibilityThemeState>) => {
    setConfig(prev => ({
      ...prev,
      accessibility: { ...prev.accessibility, ...newAccessibilityState }
    }));
  }, []);

  return (
    <ThemeStorageProvider>
      <VisualThemeProvider
        state={config.visual}
        onStateChange={handleVisualStateChange}
      >
        <LayoutThemeProvider
          state={config.layout}
          onStateChange={handleLayoutStateChange}
        >
          <AnimationThemeProvider
            state={config.animation}
            onStateChange={handleAnimationStateChange}
          >
            <AccessibilityThemeProvider
              state={config.accessibility}
              onStateChange={handleAccessibilityStateChange}
            >
              {children}
            </AccessibilityThemeProvider>
          </AnimationThemeProvider>
        </LayoutThemeProvider>
      </VisualThemeProvider>
    </ThemeStorageProvider>
  );
};

/**
 * Layout Theme Provider
 * AGENT 3: THEMECONTEXT DECOMPOSITION - Task 1.2
 * 
 * Focused context for layout and spacing management following ISP
 * Handles: density settings, spacing tokens, and layout configurations
 */

import React, { createContext, useContext, useCallback } from 'react';
import { Density } from '../ThemeContext';

// ================================
// LAYOUT THEME INTERFACES
// ================================

export interface LayoutThemeState {
  density: Density;
}

export interface DensitySpacing {
  xs: string;
  sm: string;
  md: string;
  lg: string;
  xl: string;
}

export interface LayoutThemeContextType {
  // Current State
  density: Density;

  // Layout Management
  setDensity: (density: Density) => void;

  // Utility Functions
  getDensityMultiplier: () => number;
  getSpacingTokens: () => DensitySpacing;
  getDensityClasses: () => string;
}

// ================================
// DENSITY UTILITIES
// ================================

const densityMultipliers: Record<Density, number> = {
  compact: 0.8,
  comfortable: 1,
  spacious: 1.2,
};

const calculateSpacingTokens = (multiplier: number): DensitySpacing => ({
  xs: `${0.25 * multiplier}rem`,
  sm: `${0.5 * multiplier}rem`,
  md: `${1 * multiplier}rem`,
  lg: `${1.5 * multiplier}rem`,
  xl: `${2 * multiplier}rem`,
});

// ================================
// CONTEXT SETUP
// ================================

const LayoutThemeContext = createContext<LayoutThemeContextType | null>(null);

export interface LayoutThemeProviderProps {
  children: React.ReactNode;
  state: LayoutThemeState;
  onStateChange: (newState: Partial<LayoutThemeState>) => void;
}

/**
 * Layout Theme Provider Component
 * Manages layout and spacing state following Single Responsibility Principle
 * Only handles density, spacing, and layout-related theme aspects
 */
export const LayoutThemeProvider: React.FC<LayoutThemeProviderProps> = ({
  children,
  state,
  onStateChange,
}) => {
  // Layout manipulation functions
  const setDensity = useCallback((density: Density) => {
    onStateChange({ density });
  }, [onStateChange]);

  // Utility functions
  const getDensityMultiplier = useCallback((): number => {
    return densityMultipliers[state.density];
  }, [state.density]);

  const getSpacingTokens = useCallback((): DensitySpacing => {
    const multiplier = densityMultipliers[state.density];
    return calculateSpacingTokens(multiplier);
  }, [state.density]);

  const getDensityClasses = useCallback((): string => {
    return `density-${state.density}`;
  }, [state.density]);

  const contextValue: LayoutThemeContextType = {
    // Current State
    density: state.density,

    // Layout Management
    setDensity,

    // Utility Functions
    getDensityMultiplier,
    getSpacingTokens,
    getDensityClasses,
  };

  return (
    <LayoutThemeContext.Provider value={contextValue}>
      {children}
    </LayoutThemeContext.Provider>
  );
};

/**
 * Hook to access layout theme context
 * Provides type-safe access to layout theme functionality
 */
export const useLayoutTheme = (): LayoutThemeContextType => {
  const context = useContext(LayoutThemeContext);
  if (!context) {
    throw new Error('useLayoutTheme must be used within a LayoutThemeProvider');
  }
  return context;
};

/**
 * Theme Storage Provider
 * AGENT 3: THEMECONTEXT DECOMPOSITION - Task 1.5
 * 
 * Focused context for theme persistence management following ISP
 * Handles: localStorage operations, custom presets, and theme persistence
 */

import React, { createContext, useContext, useCallback } from 'react';
import { ThemeConfiguration } from '../ThemeContext';

// ================================
// THEME STORAGE INTERFACES
// ================================

export interface CustomPreset {
  name: string;
  config: ThemeConfiguration;
  created: Date;
}

export interface ThemeStorageContextType {
  // Preset Management
  saveCustomPreset: (name: string, config: ThemeConfiguration) => Promise<void>;
  loadCustomPreset: (name: string) => Promise<ThemeConfiguration | null>;
  deleteCustomPreset: (name: string) => Promise<void>;
  getCustomPresets: () => Promise<CustomPreset[]>;

  // Configuration Persistence
  saveConfiguration: (config: ThemeConfiguration) => Promise<void>;
  loadConfiguration: () => Promise<ThemeConfiguration | null>;
  clearConfiguration: () => Promise<void>;

  // Utility Functions
  exportConfiguration: (config: ThemeConfiguration) => string;
  importConfiguration: (jsonString: string) => ThemeConfiguration | null;
  getStorageInfo: () => Promise<{ used: number; available: number }>;
}

// ================================
// STORAGE UTILITIES
// ================================

const STORAGE_KEYS = {
  config: 'pokemon-theme-config',
  customPresets: 'pokemon-custom-presets',
} as const;

const safeJsonParse = <T,>(jsonString: string | null, fallback: T): T => {
  if (!jsonString) return fallback;
  try {
    return JSON.parse(jsonString);
  } catch (error) {
    console.warn('Failed to parse JSON:', error);
    return fallback;
  }
};

const safeLocalStorageOperation = async <T,>(
  operation: () => T,
  fallback: T,
  errorMessage: string
): Promise<T> => {
  try {
    return operation();
  } catch (error) {
    console.warn(errorMessage, error);
    return fallback;
  }
};

// ================================
// CONTEXT SETUP
// ================================

const ThemeStorageContext = createContext<ThemeStorageContextType | null>(null);

export interface ThemeStorageProviderProps {
  children: React.ReactNode;
}

/**
 * Theme Storage Provider Component
 * Manages theme persistence following Single Responsibility Principle
 * Only handles storage, persistence, and custom preset management
 */
export const ThemeStorageProvider: React.FC<ThemeStorageProviderProps> = ({
  children,
}) => {
  // Custom preset management
  const saveCustomPreset = useCallback(async (name: string, config: ThemeConfiguration): Promise<void> => {
    await safeLocalStorageOperation(
      () => {
        const customPresets = safeJsonParse(
          localStorage.getItem(STORAGE_KEYS.customPresets),
          {}
        );

        customPresets[name] = {
          name,
          config,
          created: new Date().toISOString(),
        };

        localStorage.setItem(
          STORAGE_KEYS.customPresets,
          JSON.stringify(customPresets)
        );
      },
      undefined,
      'Failed to save custom preset:'
    );
  }, []);

  const loadCustomPreset = useCallback(async (name: string): Promise<ThemeConfiguration | null> => {
    return safeLocalStorageOperation(
      () => {
        const customPresets = safeJsonParse(
          localStorage.getItem(STORAGE_KEYS.customPresets),
          {}
        );

        const preset = customPresets[name];
        return preset ? preset.config : null;
      },
      null,
      'Failed to load custom preset:'
    );
  }, []);

  const deleteCustomPreset = useCallback(async (name: string): Promise<void> => {
    await safeLocalStorageOperation(
      () => {
        const customPresets = safeJsonParse(
          localStorage.getItem(STORAGE_KEYS.customPresets),
          {}
        );

        delete customPresets[name];

        localStorage.setItem(
          STORAGE_KEYS.customPresets,
          JSON.stringify(customPresets)
        );
      },
      undefined,
      'Failed to delete custom preset:'
    );
  }, []);

  const getCustomPresets = useCallback(async (): Promise<CustomPreset[]> => {
    return safeLocalStorageOperation(
      () => {
        const customPresets = safeJsonParse(
          localStorage.getItem(STORAGE_KEYS.customPresets),
          {}
        );

        return Object.values(customPresets).map((preset: any) => ({
          ...preset,
          created: new Date(preset.created),
        }));
      },
      [],
      'Failed to get custom presets:'
    );
  }, []);

  // Configuration persistence
  const saveConfiguration = useCallback(async (config: ThemeConfiguration): Promise<void> => {
    await safeLocalStorageOperation(
      () => {
        localStorage.setItem(STORAGE_KEYS.config, JSON.stringify(config));
      },
      undefined,
      'Failed to save theme configuration:'
    );
  }, []);

  const loadConfiguration = useCallback(async (): Promise<ThemeConfiguration | null> => {
    return safeLocalStorageOperation(
      () => {
        const stored = localStorage.getItem(STORAGE_KEYS.config);
        return stored ? JSON.parse(stored) : null;
      },
      null,
      'Failed to load theme configuration:'
    );
  }, []);

  const clearConfiguration = useCallback(async (): Promise<void> => {
    await safeLocalStorageOperation(
      () => {
        localStorage.removeItem(STORAGE_KEYS.config);
      },
      undefined,
      'Failed to clear theme configuration:'
    );
  }, []);

  // Utility functions
  const exportConfiguration = useCallback((config: ThemeConfiguration): string => {
    try {
      return JSON.stringify(config, null, 2);
    } catch (error) {
      console.warn('Failed to export configuration:', error);
      return '{}';
    }
  }, []);

  const importConfiguration = useCallback((jsonString: string): ThemeConfiguration | null => {
    try {
      const config = JSON.parse(jsonString);
      // Basic validation - ensure it has required properties
      if (config && typeof config === 'object' && config.visualTheme) {
        return config as ThemeConfiguration;
      }
      return null;
    } catch (error) {
      console.warn('Failed to import configuration:', error);
      return null;
    }
  }, []);

  const getStorageInfo = useCallback(async (): Promise<{ used: number; available: number }> => {
    return safeLocalStorageOperation(
      () => {
        let used = 0;
        for (const key in localStorage) {
          if (localStorage.hasOwnProperty(key)) {
            used += localStorage[key].length + key.length;
          }
        }

        // Estimate available storage (most browsers limit localStorage to ~5-10MB)
        const estimated = 5 * 1024 * 1024; // 5MB estimate
        return {
          used,
          available: Math.max(0, estimated - used),
        };
      },
      { used: 0, available: 0 },
      'Failed to get storage info:'
    );
  }, []);

  const contextValue: ThemeStorageContextType = {
    // Preset Management
    saveCustomPreset,
    loadCustomPreset,
    deleteCustomPreset,
    getCustomPresets,

    // Configuration Persistence
    saveConfiguration,
    loadConfiguration,
    clearConfiguration,

    // Utility Functions
    exportConfiguration,
    importConfiguration,
    getStorageInfo,
  };

  return (
    <ThemeStorageContext.Provider value={contextValue}>
      {children}
    </ThemeStorageContext.Provider>
  );
};

/**
 * Hook to access theme storage context
 * Provides type-safe access to theme storage functionality
 */
export const useThemeStorage = (): ThemeStorageContextType => {
  const context = useContext(ThemeStorageContext);
  if (!context) {
    throw new Error('useThemeStorage must be used within a ThemeStorageProvider');
  }
  return context;
};

/**
 * Visual Theme Provider
 * AGENT 3: THEMECONTEXT DECOMPOSITION - Task 1.1
 * 
 * Focused context for visual theme management following ISP
 * Handles: visual themes, presets, and visual appearance settings
 */

import React, { createContext, useContext, useCallback } from 'react';
import { VisualTheme, ThemePreset, themePresets } from '../ThemeContext';

// ================================
// VISUAL THEME INTERFACES
// ================================

export interface VisualThemeState {
  visualTheme: VisualTheme;
  glassmorphismIntensity: number; // 0-100
  particleEffectsEnabled: boolean;
}

export interface VisualThemeContextType {
  // Current State
  visualTheme: VisualTheme;
  glassmorphismIntensity: number;
  particleEffectsEnabled: boolean;

  // Available Presets
  presets: ThemePreset[];

  // Theme Management
  setVisualTheme: (theme: VisualTheme) => void;
  setGlassmorphismIntensity: (intensity: number) => void;
  toggleParticleEffects: () => void;

  // Preset Management
  applyPreset: (presetId: VisualTheme) => void;
  getPreset: (presetId: VisualTheme) => ThemePreset | undefined;
}

// ================================
// CONTEXT SETUP
// ================================

const VisualThemeContext = createContext<VisualThemeContextType | null>(null);

export interface VisualThemeProviderProps {
  children: React.ReactNode;
  state: VisualThemeState;
  onStateChange: (newState: Partial<VisualThemeState>) => void;
}

/**
 * Visual Theme Provider Component
 * Manages visual theme state and provides theme manipulation functions
 * Following Single Responsibility Principle - only handles visual appearance
 */
export const VisualThemeProvider: React.FC<VisualThemeProviderProps> = ({
  children,
  state,
  onStateChange,
}) => {
  // Theme manipulation functions
  const setVisualTheme = useCallback((theme: VisualTheme) => {
    onStateChange({ visualTheme: theme });
  }, [onStateChange]);

  const setGlassmorphismIntensity = useCallback((intensity: number) => {
    const clampedIntensity = Math.max(0, Math.min(100, intensity));
    onStateChange({ glassmorphismIntensity: clampedIntensity });
  }, [onStateChange]);

  const toggleParticleEffects = useCallback(() => {
    onStateChange({ particleEffectsEnabled: !state.particleEffectsEnabled });
  }, [onStateChange, state.particleEffectsEnabled]);

  // Preset management
  const applyPreset = useCallback((presetId: VisualTheme) => {
    const preset = themePresets.find((p) => p.id === presetId);
    if (preset && preset.config) {
      const updates: Partial<VisualThemeState> = {};

      if (preset.config.visualTheme !== undefined) {
        updates.visualTheme = preset.config.visualTheme;
      }
      if (preset.config.glassmorphismIntensity !== undefined) {
        updates.glassmorphismIntensity = preset.config.glassmorphismIntensity;
      }
      if (preset.config.particleEffectsEnabled !== undefined) {
        updates.particleEffectsEnabled = preset.config.particleEffectsEnabled;
      }

      onStateChange(updates);
    }
  }, [onStateChange]);

  const getPreset = useCallback((presetId: VisualTheme) => {
    return themePresets.find((p) => p.id === presetId);
  }, []);

  const contextValue: VisualThemeContextType = {
    // Current State
    visualTheme: state.visualTheme,
    glassmorphismIntensity: state.glassmorphismIntensity,
    particleEffectsEnabled: state.particleEffectsEnabled,

    // Available Presets
    presets: themePresets,

    // Theme Management
    setVisualTheme,
    setGlassmorphismIntensity,
    toggleParticleEffects,

    // Preset Management
    applyPreset,
    getPreset,
  };

  return (
    <VisualThemeContext.Provider value={contextValue}>
      {children}
    </VisualThemeContext.Provider>
  );
};

/**
 * Hook to access visual theme context
 * Provides type-safe access to visual theme functionality
 */
export const useVisualTheme = (): VisualThemeContextType => {
  const context = useContext(VisualThemeContext);
  if (!context) {
    throw new Error('useVisualTheme must be used within a VisualThemeProvider');
  }
  return context;
};

/**
 * Composite Theme Hook
 * AGENT 3: THEMECONTEXT DECOMPOSITION - Task 3
 * 
 * Composite hook that aggregates all focused theme hooks
 * Maintains backward compatibility with existing useTheme interface
 * Enables gradual migration path for components
 */

import { useCallback } from 'react';
import { useTheme as useNextTheme } from 'next-themes';
import { ThemeContextType, ThemeConfiguration, ColorScheme, ThemeColor } from '../../contexts/ThemeContext';
import { useVisualTheme } from './useVisualTheme';
import { useLayoutTheme } from './useLayoutTheme';
import { useAnimationTheme } from './useAnimationTheme';
import { useAccessibilityTheme } from './useAccessibilityTheme';
import { useThemeStorage } from './useThemeStorage';
import { formThemes } from '../../theme/formThemes';

/**
 * Composite Theme Hook
 * Aggregates all focused theme hooks into a single interface
 * Maintains backward compatibility with the original monolithic useTheme
 */
export const useTheme = (): ThemeContextType => {
  // Get focused theme contexts
  const visualTheme = useVisualTheme();
  const layoutTheme = useLayoutTheme();
  const animationTheme = useAnimationTheme();
  const accessibilityTheme = useAccessibilityTheme();
  const themeStorage = useThemeStorage();

  // Get next-themes context for color scheme management
  const { setTheme: setNextTheme, resolvedTheme } = useNextTheme();

  // Build composite configuration object
  const config: ThemeConfiguration = {
    // Visual settings
    visualTheme: visualTheme.visualTheme,
    glassmorphismIntensity: visualTheme.glassmorphismIntensity,
    particleEffectsEnabled: visualTheme.particleEffectsEnabled,

    // Layout settings
    density: layoutTheme.density,

    // Animation settings
    animationIntensity: animationTheme.animationIntensity,

    // Accessibility settings
    highContrast: accessibilityTheme.highContrast,
    reducedMotion: accessibilityTheme.reducedMotion,

    // Default values for properties not yet decomposed
    colorScheme: 'system' as ColorScheme,
    primaryColor: 'dark' as ThemeColor,
  };

  // Color scheme management (simplified for backward compatibility)
  const setColorScheme = useCallback((scheme: ColorScheme) => {
    setNextTheme(scheme);
  }, [setNextTheme]);

  // Primary color management (simplified for backward compatibility)
  const setPrimaryColor = useCallback((color: ThemeColor) => {
    // This would need to be handled by a color theme provider in a complete implementation
    console.warn('setPrimaryColor not yet implemented in decomposed context');
  }, []);

  // Utility functions that combine multiple focused contexts
  const getThemeClasses = useCallback((): string => {
    const classes = [
      `theme-${visualTheme.visualTheme}`,
      layoutTheme.getDensityClasses(),
      animationTheme.getAnimationClasses(),
      accessibilityTheme.getAccessibilityClasses(),
    ];

    if (!visualTheme.particleEffectsEnabled) {
      classes.push('no-particles');
    }

    return classes.join(' ');
  }, [visualTheme, layoutTheme, animationTheme, accessibilityTheme]);

  const getCSSProperties = useCallback((): Record<string, string> => {
    const formTheme = formThemes[config.primaryColor];
    const densityMultiplier = layoutTheme.getDensityMultiplier();
    const animationDurations = animationTheme.getAnimationDurations();
    const animationDelays = animationTheme.getAnimationDelays();
    const complexDurations = animationTheme.getComplexAnimationDurations();
    const spacingTokens = layoutTheme.getSpacingTokens();

    const properties: Record<string, string> = {
      // Form theme properties
      '--theme-primary-gradient': formTheme.button.primary.replace('bg-gradient-to-r ', ''),
      '--theme-primary-hover': formTheme.button.primaryHover.replace('hover:', ''),
      '--theme-header-background': formTheme.header.background,
      '--theme-border-color': formTheme.element.border,
      '--theme-focus-ring': formTheme.element.focus,

      // Density properties
      '--density-multiplier': densityMultiplier.toString(),
      '--density-spacing-xs': spacingTokens.xs,
      '--density-spacing-sm': spacingTokens.sm,
      '--density-spacing-md': spacingTokens.md,
      '--density-spacing-lg': spacingTokens.lg,
      '--density-spacing-xl': spacingTokens.xl,

      // Animation properties
      '--animation-duration-fast': animationDurations.fast,
      '--animation-duration-normal': animationDurations.normal,
      '--animation-duration-slow': animationDurations.slow,
      '--animation-delay-short': animationDelays.short,
      '--animation-delay-medium': animationDelays.medium,
      '--animation-delay-long': animationDelays.long,
      '--animation-duration-orbit': complexDurations.orbit,
      '--animation-duration-particle': complexDurations.particle,

      // Visual properties
      '--glass-alpha': (visualTheme.glassmorphismIntensity / 100).toString(),
      '--glass-blur': `${visualTheme.glassmorphismIntensity / 5}px`,
    };

    // Override animation durations if reduced motion is enabled
    if (accessibilityTheme.reducedMotion) {
      properties['--animation-duration-fast'] = '0s';
      properties['--animation-duration-normal'] = '0s';
      properties['--animation-duration-slow'] = '0s';
    }

    return properties;
  }, [config, visualTheme, layoutTheme, animationTheme, accessibilityTheme]);

  // System theme utilities
  const getSystemPreference = useCallback((): 'light' | 'dark' => {
    if (typeof window === 'undefined') {
      return 'dark';
    }
    return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
  }, []);

  const resetToDefaults = useCallback(() => {
    // Reset all focused contexts to defaults
    visualTheme.setVisualTheme('context7-premium');
    visualTheme.setGlassmorphismIntensity(80);
    if (visualTheme.particleEffectsEnabled !== true) {
      visualTheme.toggleParticleEffects();
    }

    layoutTheme.setDensity('comfortable');
    animationTheme.setAnimationIntensity('normal');

    if (accessibilityTheme.highContrast) {
      accessibilityTheme.toggleHighContrast();
    }
    if (accessibilityTheme.reducedMotion) {
      accessibilityTheme.toggleReducedMotion();
    }

    setNextTheme('system');
  }, [visualTheme, layoutTheme, animationTheme, accessibilityTheme, setNextTheme]);

  // Build complete ThemeContextType interface for backward compatibility
  const contextValue: ThemeContextType = {
    // Current Configuration
    config,
    resolvedTheme: (resolvedTheme as 'light' | 'dark') || 'dark',

    // Theme Management (delegated to focused providers)
    setVisualTheme: visualTheme.setVisualTheme,
    setColorScheme,
    setDensity: layoutTheme.setDensity,
    setAnimationIntensity: animationTheme.setAnimationIntensity,
    setPrimaryColor,

    // Accessibility (delegated to accessibility provider)
    toggleHighContrast: accessibilityTheme.toggleHighContrast,
    toggleReducedMotion: accessibilityTheme.toggleReducedMotion,

    // Advanced Configuration (delegated to visual provider)
    setGlassmorphismIntensity: visualTheme.setGlassmorphismIntensity,
    toggleParticleEffects: visualTheme.toggleParticleEffects,
    setCustomProperties: () => console.warn('setCustomProperties not yet implemented in decomposed context'),

    // Preset Management (delegated to visual and storage providers)
    applyPreset: visualTheme.applyPreset,
    saveCustomPreset: themeStorage.saveCustomPreset,
    loadCustomPreset: async (name: string) => {
      const config = await themeStorage.loadCustomPreset(name);
      if (config) {
        // Apply loaded configuration to all providers
        // This would need more sophisticated coordination in a complete implementation
        console.warn('loadCustomPreset partial implementation');
      }
    },
    resetToDefaults,

    // Utility Functions (composed from focused providers)
    getThemeClasses,
    getCSSProperties,
    isThemeLoaded: true, // Simplified for now

    // System Integration
    getSystemPreference,
    isSystemTheme: config.colorScheme === 'system',
  };

  return contextValue;
};

/**
 * Accessibility Theme Hook
 * Phase 3.2.2: Accessibility theme features ONLY
 *
 * Following CLAUDE.md principles:
 * - Single Responsibility: Accessibility theme management only
 * - Open/Closed: Extensible for new accessibility patterns
 * - DRY: Centralized accessibility theme logic
 * - Dependency Inversion: Abstracts accessibility implementation
 *
 * Integrates with:
 * - ThemeContext.tsx for theme state management
 * - useThemeSwitch.ts for theme switching utilities
 * - AccessibilityTheme.tsx for component rendering
 */

import { useCallback, useEffect, useState, useRef } from 'react';
import { useCentralizedTheme } from '../utils/themeConfig';
import { useAccessibilityTheme as useAccessibilityProvider } from '../contexts/theme/AccessibilityThemeProvider';

// ================================
// ACCESSIBILITY INTERFACES
// ================================

export interface AccessibilitySystemPreferences {
  /** System prefers high contrast */
  prefersHighContrast: boolean;
  /** System prefers reduced motion */
  prefersReducedMotion: boolean;
  /** System color scheme preference */
  prefersColorScheme: 'light' | 'dark' | 'no-preference';
  /** System prefers reduced transparency */
  prefersReducedTransparency: boolean;
  /** System prefers reduced data usage */
  prefersReducedData: boolean;
}

export interface AccessibilityThemeConfig {
  /** Enable automatic system preference detection */
  autoDetectPreferences?: boolean;
  /** Enable accessibility keyboard shortcuts */
  enableKeyboardShortcuts?: boolean;
  /** High contrast color overrides */
  highContrastOverrides?: {
    background?: string;
    text?: string;
    border?: string;
    accent?: string;
  };
  /** Reduced motion configuration */
  reducedMotionOverrides?: {
    disableTransitions?: boolean;
    disableAnimations?: boolean;
    disableParallax?: boolean;
    disableAutoplay?: boolean;
  };
  /** Focus management settings */
  focusManagement?: {
    trapFocus?: boolean;
    restoreFocus?: boolean;
    skipLinks?: boolean;
  };
}

export interface AccessibilityThemeActions {
  /** Toggle high contrast mode */
  toggleHighContrast: () => void;
  /** Toggle reduced motion mode */
  toggleReducedMotion: () => void;
  /** Set high contrast with custom options */
  setHighContrast: (
    enabled: boolean,
    overrides?: AccessibilityThemeConfig['highContrastOverrides']
  ) => void;
  /** Set reduced motion with custom options */
  setReducedMotion: (
    enabled: boolean,
    overrides?: AccessibilityThemeConfig['reducedMotionOverrides']
  ) => void;
  /** Sync with system preferences */
  syncWithSystemPreferences: () => void;
  /** Reset accessibility settings */
  resetAccessibilitySettings: () => void;
  /** Apply accessibility preset */
  applyAccessibilityPreset: (
    preset: 'maximum' | 'moderate' | 'minimal' | 'off'
  ) => void;
}

export interface AccessibilityThemeState {
  /** Current accessibility configuration */
  config: AccessibilityThemeConfig;
  /** System preferences */
  systemPreferences: AccessibilitySystemPreferences | null;
  /** Accessibility mode active */
  isAccessibilityModeActive: boolean;
  /** High contrast active */
  isHighContrastActive: boolean;
  /** Reduced motion active */
  isReducedMotionActive: boolean;
  /** Focus management active */
  isFocusManagementActive: boolean;
}

// ================================
// MAIN ACCESSIBILITY THEME HOOK
// ================================

/**
 * Main accessibility theme hook
 * Provides comprehensive accessibility theme management
 */
function useAccessibilityTheme(
  config: AccessibilityThemeConfig = {}
): AccessibilityThemeState & AccessibilityThemeActions {
  const themeConfig = useCentralizedTheme();
  const accessibilityProvider = useAccessibilityProvider();
  const [systemPreferences, setSystemPreferences] =
    useState<AccessibilitySystemPreferences | null>(null);
  const [accessibilityConfig, setAccessibilityConfig] =
    useState<AccessibilityThemeConfig>(config);
  const previousFocusRef = useRef<HTMLElement | null>(null);

  // ================================
  // SYSTEM PREFERENCE DETECTION
  // ================================

  useEffect(() => {
    if (!config.autoDetectPreferences) {
      return;
    }

    const detectSystemPreferences = () => {
      const preferences: AccessibilitySystemPreferences = {
        prefersHighContrast: window.matchMedia('(prefers-contrast: high)')
          .matches,
        prefersReducedMotion: window.matchMedia(
          '(prefers-reduced-motion: reduce)'
        ).matches,
        prefersColorScheme: window.matchMedia('(prefers-color-scheme: dark)')
          .matches
          ? 'dark'
          : window.matchMedia('(prefers-color-scheme: light)').matches
            ? 'light'
            : 'no-preference',
        prefersReducedTransparency: window.matchMedia(
          '(prefers-reduced-transparency: reduce)'
        ).matches,
        prefersReducedData: window.matchMedia('(prefers-reduced-data: reduce)')
          .matches,
      };

      setSystemPreferences(preferences);
      return preferences;
    };

    // Initial detection
    const initialPreferences = detectSystemPreferences();

    // Set up media query listeners
    const mediaQueries = [
      {
        query: '(prefers-contrast: high)',
        handler: () => detectSystemPreferences(),
      },
      {
        query: '(prefers-reduced-motion: reduce)',
        handler: () => detectSystemPreferences(),
      },
      {
        query: '(prefers-color-scheme: dark)',
        handler: () => detectSystemPreferences(),
      },
      {
        query: '(prefers-color-scheme: light)',
        handler: () => detectSystemPreferences(),
      },
      {
        query: '(prefers-reduced-transparency: reduce)',
        handler: () => detectSystemPreferences(),
      },
      {
        query: '(prefers-reduced-data: reduce)',
        handler: () => detectSystemPreferences(),
      },
    ];

    const mediaQueryLists = mediaQueries.map(({ query, handler }) => {
      const mql = window.matchMedia(query);
      mql.addEventListener('change', handler);
      return { mql, handler };
    });

    // Auto-apply system preferences if enabled
    if (initialPreferences.prefersHighContrast && !themeConfig.highContrast) {
      accessibilityProvider.toggleHighContrast();
    }
    if (
      initialPreferences.prefersReducedMotion &&
      !themeConfig.reducedMotion
    ) {
      accessibilityProvider.toggleReducedMotion();
    }

    return () => {
      mediaQueryLists.forEach(({ mql, handler }) => {
        mql.removeEventListener('change', handler);
      });
    };
  }, [config.autoDetectPreferences, themeConfig, accessibilityProvider]);

  // ================================
  // KEYBOARD SHORTCUTS
  // ================================

  useEffect(() => {
    if (!config.enableKeyboardShortcuts) {
      return;
    }

    const handleKeyPress = (event: KeyboardEvent) => {
      // Only activate with Ctrl/Cmd + Alt modifier for accessibility shortcuts
      if (!(event.ctrlKey || event.metaKey) || !event.altKey) {
        return;
      }

      switch (event.key.toLowerCase()) {
        case 'c':
          event.preventDefault();
          toggleHighContrast();
          announceAccessibilityChange('High contrast toggled');
          break;
        case 'm':
          event.preventDefault();
          toggleReducedMotion();
          announceAccessibilityChange('Reduced motion toggled');
          break;
        case 'r':
          event.preventDefault();
          resetAccessibilitySettings();
          announceAccessibilityChange('Accessibility settings reset');
          break;
        case 's':
          event.preventDefault();
          syncWithSystemPreferences();
          announceAccessibilityChange('Synced with system preferences');
          break;
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [config.enableKeyboardShortcuts]);

  // ================================
  // FOCUS MANAGEMENT
  // ================================

  const manageFocus = useCallback(() => {
    if (!config.focusManagement?.restoreFocus) {
      return;
    }

    // Store current focused element
    if (document.activeElement && document.activeElement !== document.body) {
      previousFocusRef.current = document.activeElement as HTMLElement;
    }
  }, [config.focusManagement?.restoreFocus]);

  const _restoreFocus = useCallback(() => {
    if (!config.focusManagement?.restoreFocus || !previousFocusRef.current) {
      return;
    }

    try {
      previousFocusRef.current.focus();
      previousFocusRef.current = null;
    } catch (error) {
      console.warn('Failed to restore focus:', error);
    }
  }, [config.focusManagement?.restoreFocus]);

  // ================================
  // ACCESSIBILITY ACTIONS
  // ================================

  const toggleHighContrast = useCallback(() => {
    accessibilityProvider.toggleHighContrast();
    manageFocus();
  }, [accessibilityProvider, manageFocus]);

  const toggleReducedMotion = useCallback(() => {
    accessibilityProvider.toggleReducedMotion();
    manageFocus();
  }, [accessibilityProvider, manageFocus]);

  const setHighContrast = useCallback(
    (
      enabled: boolean,
      overrides?: AccessibilityThemeConfig['highContrastOverrides']
    ) => {
      if (enabled !== themeConfig.highContrast) {
        accessibilityProvider.toggleHighContrast();
      }

      if (overrides) {
        setAccessibilityConfig((prev) => ({
          ...prev,
          highContrastOverrides: overrides,
        }));
      }

      manageFocus();
    },
    [themeConfig.highContrast, accessibilityProvider, manageFocus]
  );

  const setReducedMotion = useCallback(
    (
      enabled: boolean,
      overrides?: AccessibilityThemeConfig['reducedMotionOverrides']
    ) => {
      if (enabled !== themeConfig.reducedMotion) {
        accessibilityProvider.toggleReducedMotion();
      }

      if (overrides) {
        setAccessibilityConfig((prev) => ({
          ...prev,
          reducedMotionOverrides: overrides,
        }));
      }

      manageFocus();
    },
    [themeConfig.reducedMotion, accessibilityProvider, manageFocus]
  );

  const syncWithSystemPreferences = useCallback(() => {
    if (!systemPreferences) {
      return;
    }

    let changes = false;

    if (systemPreferences.prefersHighContrast && !themeConfig.highContrast) {
      accessibilityProvider.toggleHighContrast();
      changes = true;
    }

    if (systemPreferences.prefersReducedMotion && !themeConfig.reducedMotion) {
      accessibilityProvider.toggleReducedMotion();
      changes = true;
    }

    if (changes) {
      announceAccessibilityChange(
        'Synced with system accessibility preferences'
      );
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [systemPreferences, themeConfig, accessibilityProvider]);

  const resetAccessibilitySettings = useCallback(() => {
    if (themeConfig.highContrast) {
      accessibilityProvider.toggleHighContrast();
    }
    if (themeConfig.reducedMotion) {
      accessibilityProvider.toggleReducedMotion();
    }

    setAccessibilityConfig({
      autoDetectPreferences: true,
      enableKeyboardShortcuts: true,
    });

    announceAccessibilityChange('Accessibility settings reset to defaults');
    manageFocus();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [themeConfig, accessibilityProvider, manageFocus]);

  const applyAccessibilityPreset = useCallback(
    (preset: 'maximum' | 'moderate' | 'minimal' | 'off') => {
      switch (preset) {
        case 'maximum':
          setHighContrast(true, {
            background: '#000000',
            text: '#ffffff',
            border: '#ffffff',
            accent: '#ffff00',
          });
          setReducedMotion(true, {
            disableTransitions: true,
            disableAnimations: true,
            disableParallax: true,
            disableAutoplay: true,
          });
          break;

        case 'moderate':
          setHighContrast(true);
          setReducedMotion(true, {
            disableTransitions: false,
            disableAnimations: true,
            disableParallax: true,
            disableAutoplay: false,
          });
          break;

        case 'minimal':
          setHighContrast(false);
          setReducedMotion(true, {
            disableTransitions: false,
            disableAnimations: false,
            disableParallax: true,
            disableAutoplay: true,
          });
          break;

        case 'off':
          resetAccessibilitySettings();
          break;
      }

      announceAccessibilityChange(`Applied ${preset} accessibility preset`);
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [setHighContrast, setReducedMotion, resetAccessibilitySettings]
  );

  // ================================
  // ACCESSIBILITY ANNOUNCEMENTS
  // ================================

  const announceAccessibilityChange = useCallback((message: string) => {
    // Create a live region announcement for screen readers
    const announcement = document.createElement('div');
    announcement.setAttribute('aria-live', 'polite');
    announcement.setAttribute('aria-atomic', 'true');
    announcement.className = 'sr-only';
    announcement.textContent = message;

    document.body.appendChild(announcement);

    // Remove the announcement after a delay
    setTimeout(() => {
      if (announcement.parentNode) {
        announcement.parentNode.removeChild(announcement);
      }
    }, 1000);
  }, []);

  // ================================
  // DERIVED STATE
  // ================================

  const isAccessibilityModeActive =
    themeConfig.highContrast || themeConfig.reducedMotion;
  const isHighContrastActive = themeConfig.highContrast;
  const isReducedMotionActive = themeConfig.reducedMotion;
  const isFocusManagementActive = Boolean(
    config.focusManagement?.trapFocus || config.focusManagement?.restoreFocus
  );

  return {
    // State
    config: accessibilityConfig,
    systemPreferences,
    isAccessibilityModeActive,
    isHighContrastActive,
    isReducedMotionActive,
    isFocusManagementActive,

    // Actions
    toggleHighContrast,
    toggleReducedMotion,
    setHighContrast,
    setReducedMotion,
    syncWithSystemPreferences,
    resetAccessibilitySettings,
    applyAccessibilityPreset,
  };
}

// ================================
// HIGH CONTRAST THEME HOOK
// ================================

export interface HighContrastConfig {
  /** High contrast intensity (1-10) */
  intensity?: number;
  /** Color overrides */
  colorOverrides?: {
    background?: string;
    foreground?: string;
    accent?: string;
    border?: string;
  };
  /** Auto-apply system preference */
  autoApply?: boolean;
}

/**
 * High Contrast Theme Hook
 * Specialized hook for high contrast mode management
 */
function useHighContrastTheme(config: HighContrastConfig = {}) {
  const themeConfig = useCentralizedTheme();
  const accessibilityProvider = useAccessibilityProvider();
  const [intensity, setIntensity] = useState(config.intensity || 5);
  const [colorOverrides, setColorOverrides] = useState(config.colorOverrides);

  // Monitor system high contrast preference
  useEffect(() => {
    if (!config.autoApply) {
      return;
    }

    const contrastQuery = window.matchMedia('(prefers-contrast: high)');

    const handleContrastChange = () => {
      if (contrastQuery.matches && !themeConfig.highContrast) {
        accessibilityProvider.toggleHighContrast();
      }
    };

    handleContrastChange(); // Initial check
    contrastQuery.addEventListener('change', handleContrastChange);

    return () =>
      contrastQuery.removeEventListener('change', handleContrastChange);
  }, [config.autoApply, themeConfig.highContrast, accessibilityProvider]);

  const adjustIntensity = useCallback((newIntensity: number) => {
    const clampedIntensity = Math.max(1, Math.min(10, newIntensity));
    setIntensity(clampedIntensity);

    // Update CSS custom property
    if (typeof document !== 'undefined') {
      document.documentElement.style.setProperty(
        '--accessibility-contrast-intensity',
        (clampedIntensity / 5).toString()
      );
    }
  }, []);

  const updateColorOverrides = useCallback(
    (overrides: HighContrastConfig['colorOverrides']) => {
      setColorOverrides(overrides);

      if (typeof document !== 'undefined' && overrides) {
        const root = document.documentElement;

        Object.entries(overrides).forEach(([key, value]) => {
          if (value) {
            root.style.setProperty(`--accessibility-hc-${key}`, value);
          }
        });
      }
    },
    []
  );

  const resetHighContrast = useCallback(() => {
    setIntensity(5);
    setColorOverrides(undefined);

    if (typeof document !== 'undefined') {
      const root = document.documentElement;
      root.style.removeProperty('--accessibility-contrast-intensity');
      root.style.removeProperty('--accessibility-hc-background');
      root.style.removeProperty('--accessibility-hc-foreground');
      root.style.removeProperty('--accessibility-hc-accent');
      root.style.removeProperty('--accessibility-hc-border');
    }
  }, []);

  return {
    isHighContrastEnabled: themeConfig.highContrast,
    intensity,
    colorOverrides,
    adjustIntensity,
    updateColorOverrides,
    toggleHighContrast: accessibilityProvider.toggleHighContrast,
    resetHighContrast,
  };
}

// ================================
// REDUCED MOTION THEME HOOK
// ================================

export interface ReducedMotionConfig {
  /** Motion sensitivity level (1-5, 5 = most sensitive) */
  sensitivityLevel?: number;
  /** Specific motion preferences */
  motionPreferences?: {
    allowEssentialMotion?: boolean;
    allowHoverEffects?: boolean;
    allowFocusEffects?: boolean;
    allowScrollAnimations?: boolean;
  };
  /** Auto-apply system preference */
  autoApply?: boolean;
}

/**
 * Reduced Motion Theme Hook
 * Specialized hook for motion sensitivity management
 */
function useReducedMotionTheme(config: ReducedMotionConfig = {}) {
  const themeConfig = useCentralizedTheme();
  const accessibilityProvider = useAccessibilityProvider();
  const [sensitivityLevel, setSensitivityLevel] = useState(
    config.sensitivityLevel || 3
  );
  const [motionPreferences, setMotionPreferences] = useState(
    config.motionPreferences || {
      allowEssentialMotion: true,
      allowHoverEffects: false,
      allowFocusEffects: true,
      allowScrollAnimations: false,
    }
  );

  // Monitor system reduced motion preference
  useEffect(() => {
    if (!config.autoApply) {
      return;
    }

    const motionQuery = window.matchMedia('(prefers-reduced-motion: reduce)');

    const handleMotionChange = () => {
      if (motionQuery.matches && !themeConfig.reducedMotion) {
        accessibilityProvider.toggleReducedMotion();
      }
    };

    handleMotionChange(); // Initial check
    motionQuery.addEventListener('change', handleMotionChange);

    return () => motionQuery.removeEventListener('change', handleMotionChange);
  }, [config.autoApply, themeConfig.reducedMotion, accessibilityProvider]);

  const adjustSensitivity = useCallback((newLevel: number) => {
    const clampedLevel = Math.max(1, Math.min(5, newLevel));
    setSensitivityLevel(clampedLevel);

    // Update CSS custom property
    if (typeof document !== 'undefined') {
      document.documentElement.style.setProperty(
        '--accessibility-motion-sensitivity',
        clampedLevel.toString()
      );
    }
  }, []);

  const updateMotionPreferences = useCallback(
    (preferences: ReducedMotionConfig['motionPreferences']) => {
      setMotionPreferences((prev) => ({ ...prev, ...preferences }));

      if (typeof document !== 'undefined' && preferences) {
        const root = document.documentElement;

        Object.entries(preferences).forEach(([key, value]) => {
          root.style.setProperty(
            `--accessibility-${key.replace(/([A-Z])/g, '-$1').toLowerCase()}`,
            value ? 'true' : 'false'
          );
        });
      }
    },
    []
  );

  const resetReducedMotion = useCallback(() => {
    setSensitivityLevel(3);
    setMotionPreferences({
      allowEssentialMotion: true,
      allowHoverEffects: false,
      allowFocusEffects: true,
      allowScrollAnimations: false,
    });

    if (typeof document !== 'undefined') {
      const root = document.documentElement;
      root.style.removeProperty('--accessibility-motion-sensitivity');
      root.style.removeProperty('--accessibility-allow-essential-motion');
      root.style.removeProperty('--accessibility-allow-hover-effects');
      root.style.removeProperty('--accessibility-allow-focus-effects');
      root.style.removeProperty('--accessibility-allow-scroll-animations');
    }
  }, []);

  return {
    isReducedMotionEnabled: themeConfig.reducedMotion,
    sensitivityLevel,
    motionPreferences,
    adjustSensitivity,
    updateMotionPreferences,
    toggleReducedMotion: accessibilityProvider.toggleReducedMotion,
    resetReducedMotion,
  };
}

// ================================
// FOCUS MANAGEMENT HOOK
// ================================

export interface FocusManagementConfig {
  /** Focus trap enabled */
  trapFocus?: boolean;
  /** Focus restore on unmount */
  restoreFocus?: boolean;
  /** Enhanced focus indicators */
  enhancedFocusIndicators?: boolean;
  /** Focus indicator style */
  focusIndicatorStyle?: 'ring' | 'outline' | 'glow' | 'border';
  /** Focus indicator color */
  focusIndicatorColor?: string;
  /** Focus indicator thickness */
  focusIndicatorThickness?: number;
}

/**
 * Focus Management Hook
 * Provides theme-aware focus management capabilities
 */
function useFocusManagementTheme(config: FocusManagementConfig = {}) {
  const themeConfig = useCentralizedTheme();
  const [focusHistory, setFocusHistory] = useState<HTMLElement[]>([]);
  const trapContainerRef = useRef<HTMLElement | null>(null);

  // Enhanced focus indicators
  useEffect(() => {
    if (!config.enhancedFocusIndicators) {
      return;
    }

    if (typeof document !== 'undefined') {
      const root = document.documentElement;

      root.style.setProperty('--accessibility-enhanced-focus', 'true');
      root.style.setProperty(
        '--accessibility-focus-style',
        config.focusIndicatorStyle || 'ring'
      );

      if (config.focusIndicatorColor) {
        root.style.setProperty(
          '--accessibility-focus-color',
          config.focusIndicatorColor
        );
      }

      root.style.setProperty(
        '--accessibility-focus-thickness',
        `${config.focusIndicatorThickness || 2}px`
      );
    }

    return () => {
      if (typeof document !== 'undefined') {
        const root = document.documentElement;
        root.style.removeProperty('--accessibility-enhanced-focus');
        root.style.removeProperty('--accessibility-focus-style');
        root.style.removeProperty('--accessibility-focus-color');
        root.style.removeProperty('--accessibility-focus-thickness');
      }
    };
  }, [
    config.enhancedFocusIndicators,
    config.focusIndicatorStyle,
    config.focusIndicatorColor,
    config.focusIndicatorThickness,
  ]);

  // Focus trap implementation
  useEffect(() => {
    if (!config.trapFocus || !trapContainerRef.current) {
      return;
    }

    const container = trapContainerRef.current;
    const focusableElements = container.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );

    const firstElement = focusableElements[0] as HTMLElement;
    const lastElement = focusableElements[
      focusableElements.length - 1
    ] as HTMLElement;

    const handleTabKey = (event: KeyboardEvent) => {
      if (event.key !== 'Tab') {
        return;
      }

      if (event.shiftKey) {
        if (document.activeElement === firstElement) {
          event.preventDefault();
          lastElement?.focus();
        }
      } else {
        if (document.activeElement === lastElement) {
          event.preventDefault();
          firstElement?.focus();
        }
      }
    };

    container.addEventListener('keydown', handleTabKey);

    // Focus first element when trap is enabled
    firstElement?.focus();

    return () => {
      container.removeEventListener('keydown', handleTabKey);
    };
  }, [config.trapFocus]);

  // Focus history management
  const pushFocusHistory = useCallback((element: HTMLElement) => {
    setFocusHistory((prev) => [...prev.slice(-9), element]); // Keep last 10 elements
  }, []);

  const restorePreviousFocus = useCallback(() => {
    const previousElement = focusHistory[focusHistory.length - 2];
    if (previousElement && config.restoreFocus) {
      try {
        previousElement.focus();
        setFocusHistory((prev) => prev.slice(0, -1));
      } catch (error) {
        console.warn('Failed to restore focus:', error);
      }
    }
  }, [focusHistory, config.restoreFocus]);

  return {
    trapContainerRef,
    focusHistory,
    pushFocusHistory,
    restorePreviousFocus,
    enhancedFocusEnabled: config.enhancedFocusIndicators || false,
    focusStyle: config.focusIndicatorStyle || 'ring',
  };
}

// ================================
// MOTION SENSITIVITY HOOK
// ================================

export interface MotionSensitivityConfig {
  /** Motion sensitivity level (1-5) */
  sensitivityLevel?: number;
  /** Disable specific motion types */
  disabledMotions?: {
    transitions?: boolean;
    animations?: boolean;
    transforms?: boolean;
    parallax?: boolean;
    autoplay?: boolean;
  };
  /** Allow essential motions only */
  essentialOnly?: boolean;
}

/**
 * Motion Sensitivity Hook
 * Advanced motion sensitivity management for users with vestibular disorders
 */
function useMotionSensitivity(config: MotionSensitivityConfig = {}) {
  const themeConfig = useCentralizedTheme();
  const accessibilityProvider = useAccessibilityProvider();
  const [sensitivityLevel, setSensitivityLevel] = useState(
    config.sensitivityLevel || 3
  );
  const [disabledMotions, setDisabledMotions] = useState(
    config.disabledMotions || {}
  );

  // Apply motion sensitivity settings to CSS
  useEffect(() => {
    if (!themeConfig.reducedMotion) {
      return;
    }

    if (typeof document !== 'undefined') {
      const root = document.documentElement;

      // Set sensitivity level
      root.style.setProperty(
        '--accessibility-motion-sensitivity',
        sensitivityLevel.toString()
      );

      // Apply motion restrictions
      Object.entries(disabledMotions).forEach(([motionType, disabled]) => {
        if (disabled) {
          root.style.setProperty(
            `--accessibility-disable-${motionType}`,
            'true'
          );
        }
      });

      // Essential motion only mode
      if (config.essentialOnly) {
        root.style.setProperty('--accessibility-essential-motion-only', 'true');
      }
    }

    return () => {
      if (typeof document !== 'undefined') {
        const root = document.documentElement;
        root.style.removeProperty('--accessibility-motion-sensitivity');
        root.style.removeProperty('--accessibility-essential-motion-only');

        Object.keys(disabledMotions).forEach((motionType) => {
          root.style.removeProperty(`--accessibility-disable-${motionType}`);
        });
      }
    };
  }, [
    themeConfig.reducedMotion,
    sensitivityLevel,
    disabledMotions,
    config.essentialOnly,
  ]);

  const adjustSensitivity = useCallback((newLevel: number) => {
    const clampedLevel = Math.max(1, Math.min(5, newLevel));
    setSensitivityLevel(clampedLevel);

    // Auto-adjust disabled motions based on sensitivity
    if (clampedLevel >= 4) {
      setDisabledMotions({
        transitions: true,
        animations: true,
        transforms: true,
        parallax: true,
        autoplay: true,
      });
    } else if (clampedLevel >= 3) {
      setDisabledMotions({
        transitions: false,
        animations: true,
        transforms: false,
        parallax: true,
        autoplay: true,
      });
    } else {
      setDisabledMotions({
        transitions: false,
        animations: false,
        transforms: false,
        parallax: true,
        autoplay: false,
      });
    }
  }, []);

  const toggleMotionType = useCallback(
    (
      motionType: keyof MotionSensitivityConfig['disabledMotions'],
      disabled: boolean
    ) => {
      setDisabledMotions((prev) => ({
        ...prev,
        [motionType]: disabled,
      }));
    },
    []
  );

  return {
    sensitivityLevel,
    disabledMotions,
    isReducedMotionEnabled: themeConfig.reducedMotion,
    adjustSensitivity,
    toggleMotionType,
    toggleReducedMotion: accessibilityProvider.toggleReducedMotion,
  };
}

// ================================
// ACCESSIBILITY KEYBOARD SHORTCUTS
// ================================

/**
 * Accessibility Keyboard Shortcuts Hook
 * Provides keyboard shortcuts for accessibility features
 */
function useAccessibilityKeyboardShortcuts(enabled: boolean = true) {
  const accessibility = useAccessibilityTheme();

  useEffect(() => {
    if (!enabled) {
      return;
    }

    const handleKeyPress = (event: KeyboardEvent) => {
      // Accessibility shortcuts use Ctrl/Cmd + Alt
      if (!(event.ctrlKey || event.metaKey) || !event.altKey) {
        return;
      }

      switch (event.key.toLowerCase()) {
        case 'c':
          event.preventDefault();
          accessibility.toggleHighContrast();
          break;
        case 'm':
          event.preventDefault();
          accessibility.toggleReducedMotion();
          break;
        case 'r':
          event.preventDefault();
          accessibility.resetAccessibilitySettings();
          break;
        case 's':
          event.preventDefault();
          accessibility.syncWithSystemPreferences();
          break;
        case '1':
          event.preventDefault();
          accessibility.applyAccessibilityPreset('minimal');
          break;
        case '2':
          event.preventDefault();
          accessibility.applyAccessibilityPreset('moderate');
          break;
        case '3':
          event.preventDefault();
          accessibility.applyAccessibilityPreset('maximum');
          break;
        case '0':
          event.preventDefault();
          accessibility.applyAccessibilityPreset('off');
          break;
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [enabled, accessibility]);

  return {
    shortcuts: {
      'Ctrl/Cmd + Alt + C': 'Toggle high contrast mode',
      'Ctrl/Cmd + Alt + M': 'Toggle reduced motion mode',
      'Ctrl/Cmd + Alt + R': 'Reset accessibility settings',
      'Ctrl/Cmd + Alt + S': 'Sync with system preferences',
      'Ctrl/Cmd + Alt + 1': 'Apply minimal accessibility preset',
      'Ctrl/Cmd + Alt + 2': 'Apply moderate accessibility preset',
      'Ctrl/Cmd + Alt + 3': 'Apply maximum accessibility preset',
      'Ctrl/Cmd + Alt + 0': 'Turn off accessibility features',
    },
  };
}

// ================================
// EXPORTS
// ================================

export default useAccessibilityTheme;

export {
  useAccessibilityTheme,
  useHighContrastTheme,
  useReducedMotionTheme,
  useFocusManagementTheme,
  useMotionSensitivity,
  useAccessibilityKeyboardShortcuts,
};

